// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {Encoding, Layout, Payload} from "../types/Condition.sol";

/**
 * @title AbiDecoder - Locates parameters within ABI-encoded calldata
 *
 * @author gnosisguild
 *
 * @notice Given ABI-encoded calldata and a type tree (Layout) describing its
 *         structure, this library produces a Payload tree that maps each
 *         parameter to its location and size (how many bytes it spans).
 *
 *         The decoder validates bounds but does NOT extract or interpret
 *         values. It only identifies WHERE parameters are, not WHAT they
 *         contain.
 *
 *         The resulting Payload is used by ConditionLogic for efficient
 *         calldata slicing during condition evaluation.
 */
library AbiDecoder {
    /**
     * @dev Entry point. Locates all parameters in calldata according to Layout.
     *
     * @param data     The calldata to inspect.
     * @param layout   The type tree describing the encoding structure.
     * @return payload A tree matching the encoding structure, with location
     *                 and size for each parameter.
     */
    function inspect(
        bytes calldata data,
        Layout memory layout
    ) internal pure returns (Payload memory payload) {
        assert(layout.encoding == Encoding.AbiEncoded);
        /*
         * The parameter encoding area consists of a head region, divided into
         * 32-byte chunks. Each parameter occupies one chunk in the head:
         * - Static parameters are encoded inline.
         * - Dynamic parameters store an offset pointing to the tail region, where
         *   the actual encoded data resides.
         *
         * Note: The offset is relative to the start of each block, not the start
         * of the buffer.
         */
        __block__(
            data,
            layout.leadingBytes,
            layout.children.length,
            layout,
            payload
        );
        payload.size = data.length;
    }

    /**
     * @dev Traverses a type tree, produces Payload with position and size.
     *
     * @param data     The calldata being inspected.
     * @param location Absolute byte position of this parameter.
     * @param layout   The type tree node to process.
     * @param payload  Output: populated with location, size, and children.
     */
    function _walk(
        bytes calldata data,
        uint256 location,
        Layout memory layout,
        Payload memory payload
    ) private pure {
        if (location + 32 > data.length) {
            payload.overflow = true;
            return;
        }

        payload.location = location;
        payload.inlined = layout.inlined;

        Encoding encoding = layout.encoding;
        if (encoding == Encoding.Static) {
            payload.size = 32;
        } else if (encoding == Encoding.Dynamic) {
            if (layout.children.length > 0) {
                _variant(data, location, layout, payload);
            }
            payload.size = 32 + _ceil32(uint256(word(data, location)));
        } else if (encoding == Encoding.Tuple) {
            __block__(data, location, layout.children.length, layout, payload);
        } else if (encoding == Encoding.Array) {
            __block__(
                data,
                location + 32,
                uint256(word(data, location)),
                layout,
                payload
            );
            payload.size += 32;
        } else {
            assert(encoding == Encoding.AbiEncoded);
            __block__(
                data,
                location + 32 + layout.leadingBytes,
                layout.children.length,
                layout,
                payload
            );
            payload.location = location + 32;
            payload.size = 32 + _ceil32(uint256(word(data, location)));
        }

        if (location + payload.size > data.length) {
            payload.overflow = true;
        }
    }

    /**
     * @dev Processes a sequence of elements using HEAD+TAIL encoding.
     *      Tuple, Array and AbiEncoded use this encoding type.
     *
     * @param data     The calldata being inspected.
     * @param location Byte position where the block's HEAD region starts.
     * @param length   Number of elements in the block.
     * @param layout   Type tree node for the block (Array or Tuple).
     * @param payload  Output: populated with child Payloads.
     *
     * @notice Arrays use layout.children[0] as a template for all elements.
     *         Variant arrays use layout.children[i] for each element.
     *         Tuples use layout.children[i] for each element.
     *         Static elements are encoded inline in HEAD.
     *         Dynamic elements have a pointer in HEAD, data in TAIL.
     */
    function __block__(
        bytes calldata data,
        uint256 location,
        uint256 length,
        Layout memory layout,
        Payload memory payload
    ) private pure {
        Payload[] memory children = new Payload[](length);

        bool isArray = layout.encoding == Encoding.Array;
        uint256 offset;
        for (uint256 i; i < length; ++i) {
            Layout memory childLayout = layout.children[
                isArray && i >= layout.children.length ? 0 : i
            ];
            Payload memory childPayload = children[i];

            bool isInline = childLayout.inlined;

            uint256 childLocation = _locationInBlock(
                data,
                location,
                offset,
                isInline
            );

            if (childLocation == type(uint256).max) {
                payload.overflow = true;
                return;
            }

            _walk(data, childLocation, childLayout, childPayload);

            if (childPayload.overflow) {
                payload.overflow = true;
                return;
            }

            // Update the offset in the block for the next element
            offset += isInline ? childPayload.size : 32;

            // For non-inline elements, we need to account for the 32-byte pointer
            payload.size += childPayload.size + (isInline ? 0 : 32);
        }
        payload.children = children;
    }

    /**
     * @dev Tries multiple type interpretations for the same bytes location.
     *      Handles cases where a dynamic parameter's content has multiple valid
     *      decodings.
     *
     * @param data     The calldata being inspected.
     * @param location Byte position to interpret.
     * @param layout   Type tree node with variant children.
     * @param payload  Output: marked as variant, with one child per interpretation.
     *
     * @notice Sets overflow=false if at least one interpretation succeeds.
     */
    function _variant(
        bytes calldata data,
        uint256 location,
        Layout memory layout,
        Payload memory payload
    ) private pure {
        uint256 length = layout.children.length;

        payload.variant = true;
        payload.overflow = true;
        payload.children = new Payload[](length);

        unchecked {
            for (uint256 i; i < length; ++i) {
                _walk(data, location, layout.children[i], payload.children[i]);
                payload.overflow =
                    payload.overflow &&
                    payload.children[i].overflow;
            }
        }
    }

    /**
     * @dev Computes the absolute calldata position of an element within a block.
     *
     * @param data       The calldata being inspected.
     * @param location   Start of the block's HEAD region.
     * @param headOffset Current offset within HEAD.
     * @param isInline   Whether element is stored directly in HEAD.
     * @return           Absolute position, or type(uint256).max on overflow.
     *
     * @notice Inline elements live at location + headOffset.
     *         Non-inline elements read a pointer from HEAD that points into TAIL.
     *         TAIL offsets are relative to block start.
     */
    function _locationInBlock(
        bytes calldata data,
        uint256 location,
        uint256 headOffset,
        bool isInline
    ) private pure returns (uint256) {
        // is head within calldata
        if (location + headOffset + 32 > data.length) {
            return type(uint256).max;
        }

        if (isInline) {
            return location + headOffset;
        }

        uint256 tailOffset = uint256(word(data, location + headOffset));

        // tail points within head?
        if (tailOffset <= headOffset) {
            return type(uint256).max;
        }

        // tail points beyond calldata?
        if (location + tailOffset + 32 > data.length) {
            return type(uint256).max;
        }

        return location + tailOffset;
    }

    /**
     * @dev Loads a 32-byte word from calldata.
     *
     * @param data     The calldata being inspected.
     * @param location The byte offset to read from.
     * @return result  The 32-byte word at location.
     */
    function word(
        bytes calldata data,
        uint256 location
    ) internal pure returns (bytes32 result) {
        assembly {
            result := calldataload(add(data.offset, location))
        }
    }

    /**
     * @dev Calculates the ceiling of a number to the nearest multiple of 32
     */
    function _ceil32(uint256 size) private pure returns (uint256) {
        // pad size. Source: http://www.cs.nott.ac.uk/~psarb2/G51MPC/slides/NumberLogic.pdf
        //return ((size + 32 - 1) / 32) * 32;
        return (size + 31) & ~uint256(31);
    }
}

contracts/common/AllowanceLoader.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {Allowance} from "../types/Allowance.sol";

/**
 * @title AllowanceLoader
 * @notice Loads allowances from storage and calculates accrued balances
 *
 * @author gnosisguild
 */
library AllowanceLoader {
    function accrue(
        bytes32 allowanceKey,
        uint64 blockTimestamp
    ) internal view returns (uint128 balance, uint64 timestamp) {
        return _accrue(_load(allowanceKey), blockTimestamp);
    }

    function accrue(
        Allowance memory allowance,
        uint64 blockTimestamp
    ) internal pure returns (uint128 balance, uint64 timestamp) {
        return _accrue(allowance, blockTimestamp);
    }

    function _accrue(
        Allowance memory allowance,
        uint64 blockTimestamp
    ) private pure returns (uint128 balance, uint64 timestamp) {
        if (
            allowance.period == 0 ||
            blockTimestamp < (allowance.timestamp + allowance.period)
        ) {
            return (allowance.balance, allowance.timestamp);
        }

        uint64 elapsedIntervals = (blockTimestamp - allowance.timestamp) /
            allowance.period;

        if (allowance.balance < allowance.maxRefill) {
            balance = allowance.balance + allowance.refill * elapsedIntervals;
            balance = balance < allowance.maxRefill
                ? balance
                : allowance.maxRefill;
        } else {
            balance = allowance.balance;
        }

        timestamp = allowance.timestamp + elapsedIntervals * allowance.period;
    }

    /**
     *
     * Storage Layout (2 words, 64 bytes, 512 bits):
     * ┌────────────────────────────────┬────────────────────────────────┐
     * │           maxRefill            │             refill             │
     * │            128 bits            │            128 bits            │
     * ├────────────────┬───────────────┴───────────────┬────────────────┤
     * │   timestamp    │            balance            │     period     │
     * │    64 bits     │           128 bits            │    64 bits     │
     * └────────────────┴───────────────────────────────┴────────────────┘
     *
     */
    uint256 constant ALLOWANCES_SLOT = 106;
    function _load(
        bytes32 allowanceKey
    ) private view returns (Allowance memory) {
        uint256 word1;
        uint256 word2;
        assembly {
            mstore(0x00, allowanceKey)
            mstore(0x20, ALLOWANCES_SLOT)
            let slot := keccak256(0x00, 0x40)
            word1 := sload(slot)
            word2 := sload(add(slot, 1))
        }

        return
            Allowance({
                refill: uint128(word1),
                maxRefill: uint128(word1 >> 128),
                period: uint64(word2),
                balance: uint128(word2 >> 64),
                timestamp: uint64(word2 >> 192)
            });
    }
}

contracts/common/ImmutableStorage.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

interface ISingletonFactory {
    function deploy(
        bytes memory initCode,
        bytes32 salt
    ) external returns (address);
}

library ImmutableStorage {
    address public constant SINGLETON_FACTORY =
        0xce0042B868300000d44A59004Da54A005ffdcf9f;

    function store(bytes memory buffer) internal returns (address pointer) {
        bytes memory bytecode = _creationBytecode(buffer);
        pointer = _calculateAddress(bytecode);

        uint256 size;
        assembly {
            size := extcodesize(pointer)
        }

        if (size == 0) {
            assert(
                pointer ==
                    ISingletonFactory(SINGLETON_FACTORY).deploy(
                        bytecode,
                        bytes32(0)
                    )
            );
        }
    }

    /**
     * @notice Reads the runtime bytecode stored at a `pointer` contract, skipping the first byte.
     *
     * @param pointer The address of the deployed pointer contract.
     * @return buffer The raw bytecode (minus the prepended 0x00) read from `pointer`.
     */
    function load(address pointer) internal view returns (bytes memory buffer) {
        assembly {
            let size := sub(extcodesize(pointer), 1)
            // free memory point ought to be multiple of 32
            let rounded := and(add(size, 31), not(31))

            // Get free pointer
            buffer := mload(0x40)
            // Store length
            mstore(buffer, size)
            // Update free pointer
            mstore(0x40, add(add(buffer, 0x20), rounded))

            // Copy code starting from offset 0x01 (skip the 0x00 prefix)
            extcodecopy(pointer, add(buffer, 0x20), 0x01, size)
        }
    }

    /**
     * @notice Generates creation bytecode that deploys a contract containing `data` as its runtime bytecode.
     * @dev The generated constructor copies `data` into memory and returns it as the contract's code.
     *      A leading `0x00` byte is prepended so the resulting contract cannot be called.
     *
     * Assembly layout (constructor):
     *
     * ```
     * 0x00    63 <XXXXXX>   PUSH4 code_size         ; push code length (runtime size)
     * 0x01    80             DUP1                   ; duplicate size
     * 0x02    60 0e          PUSH1 0x0e             ; offset of actual code
     * 0x03    60 00          PUSH1 0x00             ; destination offset
     * 0x04    39             CODECOPY               ; copy <code_size> bytes from offset 0x0e
     * 0x05    60 00          PUSH1 0x00             ; return offset
     * 0x06    f3             RETURN                 ; return code as runtime bytecode
     * <CODE>                                        ; runtime code (0x00 + data)
     * ```
     *
     */
    function _creationBytecode(
        bytes memory data
    ) private pure returns (bytes memory) {
        return
            abi.encodePacked(
                hex"63",
                uint32(data.length + 1),
                hex"80_60_0E_60_00_39_60_00_F3",
                // Prepend 0x00 to prevent the deployed contract from being callable
                hex"00",
                data
            );
    }

    function _calculateAddress(
        bytes memory creationBytecode
    ) private pure returns (address) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                SINGLETON_FACTORY,
                bytes32(0),
                keccak256(creationBytecode)
            )
        );
        return address(uint160(uint256(hash)));
    }
}

contracts/common/ScopeConfig.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {ExecutionOptions} from "../types/Permission.sol";

library ScopeConfig {
    /**
     * Bit-packed configuration (bytes32):
     * ┌───────────────────┬────────────┬─────────┬───────────────────────┐
     * │      (unused)     │ wildcarded │ options │        pointer        │
     * │      87 bits      │   1 bit    │ 8 bits  │        160 bits       │
     * └───────────────────┴────────────┴─────────┴───────────────────────┘
     */
    function pack(
        ExecutionOptions options,
        address pointer
    ) internal pure returns (bytes32) {
        return bytes32((uint256(options) << 160) | uint256(uint160(pointer)));
    }

    function packAsWildcarded(
        ExecutionOptions options
    ) internal pure returns (bytes32) {
        return bytes32((1 << 168) | (uint256(options) << 160));
    }

    function unpack(
        bytes32 scopeConfig
    )
        internal
        pure
        returns (bool isWildcarded, ExecutionOptions options, address pointer)
    {
        isWildcarded = (uint256(scopeConfig) & (1 << 168)) != 0;
        options = ExecutionOptions((uint256(scopeConfig) >> 160) & 0xFF);
        pointer = address(uint160(uint256(scopeConfig)));
    }
}

contracts/core/Authorization.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../common/ImmutableStorage.sol";
import "../common/ScopeConfig.sol";
import "../periphery/interfaces/ITransactionUnwrapper.sol";

import "./evaluate/ConditionLogic.sol";
import "./serialize/ConditionUnpacker.sol";
import "./Storage.sol";

/**
 * @title Authorization - Authorizes transactions based on target clearance
 *        and function scope rules.
 *
 * @author gnosisguild
 */
abstract contract Authorization is RolesStorage {
    function _authorize(
        bytes32 roleKey,
        address to,
        uint256 value,
        bytes calldata data,
        Operation operation
    ) internal view returns (Consumption[] memory) {
        Result memory result;
        Transaction memory transaction = Transaction(to, value, operation);

        address adapter = unwrappers[_key(to, bytes4(data))];
        if (adapter == address(0)) {
            result = _transaction(
                roleKey,
                data,
                result.consumptions,
                transaction
            );
        } else {
            result = _transactionBundle(adapter, roleKey, data, transaction);
        }
        if (result.status != Status.Ok) {
            revert ConditionViolation(result.status, result.info);
        }

        return result.consumptions;
    }

    function _transactionBundle(
        address adapter,
        bytes32 roleKey,
        bytes calldata data,
        Transaction memory transaction
    ) private view returns (Result memory result) {
        try
            ITransactionUnwrapper(adapter).unwrap(
                transaction.to,
                transaction.value,
                data,
                transaction.operation
            )
        returns (UnwrappedTransaction[] memory unwrapped) {
            for (uint256 i; i < unwrapped.length; ++i) {
                UnwrappedTransaction memory entry = unwrapped[i];
                uint256 left = entry.dataLocation;
                uint256 right = left + entry.dataSize;
                result = _transaction(
                    roleKey,
                    data[left:right],
                    result.consumptions,
                    Transaction(entry.to, entry.value, entry.operation)
                );
                if (result.status != Status.Ok) {
                    return result;
                }
            }
        } catch {
            revert MalformedMultiEntrypoint();
        }
    }

    /// @dev Inspects a transaction and authorizes based on role permissions.
    function _transaction(
        bytes32 roleKey,
        bytes calldata data,
        Consumption[] memory consumptions,
        Transaction memory transaction
    ) private view returns (Result memory) {
        Role storage role = roles[roleKey];

        if (data.length != 0 && data.length < 4) {
            revert FunctionSignatureTooShort();
        }

        Clearance clearance = role.clearance[transaction.to];
        if (clearance == Clearance.None) {
            return Result(Status.TargetAddressNotAllowed, consumptions, 0);
        }

        bytes32 key = bytes32(bytes20(transaction.to)) |
            (
                clearance == Clearance.Target
                    ? (~bytes32(0) >> 160)
                    : (bytes32(bytes4(data)) >> 160)
            );

        bytes32 scopeConfig = role.scopeConfig[key];
        if (scopeConfig == 0) {
            return
                Result(
                    clearance == Clearance.Target
                        ? Status.TargetAddressNotAllowed
                        : Status.FunctionNotAllowed,
                    consumptions,
                    bytes4(data)
                );
        }

        return _function(scopeConfig, data, consumptions, transaction);
    }

    /// @dev Checks function permission and evaluates conditions if present.
    function _function(
        bytes32 scopeConfig,
        bytes calldata data,
        Consumption[] memory consumptions,
        Transaction memory transaction
    ) private view returns (Result memory result) {
        (
            bool isWildcarded,
            ExecutionOptions options,
            address pointer
        ) = ScopeConfig.unpack(scopeConfig);

        /*
         * ExecutionOptions can send:
         *  options == ExecutionOptions.Send ||
         *  options == ExecutionOptions.Both
         */
        if (uint8(options) & 1 == 0 && transaction.value > 0) {
            return Result(Status.SendNotAllowed, consumptions, 0);
        }

        /*
         * ExecutionOptions can delegateCall:
         * options == ExecutionOptions.DelegateCall ||
         * options == ExecutionOptions.Both
         */
        if (
            uint8(options) & 2 == 0 &&
            transaction.operation == Operation.DelegateCall
        ) {
            return Result(Status.DelegateCallNotAllowed, consumptions, 0);
        }

        if (isWildcarded) {
            return Result(Status.Ok, consumptions, 0);
        }

        (
            Condition memory condition,
            Layout memory layout,
            uint256 maxPluckIndex
        ) = ConditionUnpacker.unpack(ImmutableStorage.load(pointer));

        Payload memory payload;
        if (layout.encoding != Encoding.None) {
            payload = AbiDecoder.inspect(data, layout);
        }

        Context memory context = Context(
            transaction.to,
            transaction.value,
            transaction.operation,
            new bytes32[](maxPluckIndex + 1)
        );

        return
            ConditionLogic.evaluate(
                data,
                condition,
                payload,
                consumptions,
                context
            );
    }
}

contracts/core/evaluate/BitmaskChecker.s.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../common/AbiDecoder.sol";

import {Status} from "../../types/Types.sol";

/**
 * @title BitmaskChecker
 * @notice Validates that a value matches an expected pattern after applying a bitmask.
 *
 * @dev compValue packs shift, mask and expected.
 *
 * @author gnosisguild
 *
 */
library BitmaskChecker {
    function check(
        bytes calldata data,
        bytes memory compValue,
        Payload memory payload
    ) internal pure returns (Status) {
        uint256 shift = uint16(bytes2(compValue));
        uint256 length = (compValue.length - 2) / 2;

        // Dynamic params: skip 32-byte length prefix.
        uint256 start = payload.location + (payload.size == 32 ? 0 : 32);
        uint256 end = data.length;

        if (shift + length > end - start) {
            return Status.BitmaskOverflow;
        }

        bytes calldata value = data[start:];

        /*
         * Load actual, expected, and mask in 32-byte chunks. Final chunk
         * gets rinsed if less than 32 bytes remain.
         */
        for (uint256 i; i < length; i += 32) {
            /*
             * compValue memory layout:
             * | 32: length | 2: shift | N: mask | N: expected |
             *                         ^--- 0x22 offset
             */
            bytes32 mask;
            assembly {
                mask := mload(add(add(compValue, 0x22), i))
            }

            bytes32 expected;
            assembly {
                expected := mload(add(add(compValue, 0x22), add(length, i)))
            }

            bytes32 actual;
            assembly {
                actual := calldataload(add(value.offset, add(shift, i)))
            }

            bytes32 rinseMask = _rinseMask(length - i);

            if (expected & rinseMask != actual & mask & rinseMask) {
                return Status.BitmaskNotAllowed;
            }
        }

        return Status.Ok;
    }

    /// @dev Returns a mask with leading 1s for byteCount bytes, rest 0s.
    function _rinseMask(uint256 bytesLeft) private pure returns (bytes32) {
        uint256 bitsToRinse = (bytesLeft > 31 ? 0 : 32 - bytesLeft) * 8;
        return bytes32(type(uint256).max << bitsToRinse);
    }
}

contracts/core/evaluate/ConditionLogic.s.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./BitmaskChecker.sol";
import "./WithinAllowanceChecker.sol";
import "./WithinRatioChecker.sol";

import "../../periphery/interfaces/ICustomCondition.sol";

import "../../types/Types.sol";

/**
 * @title ConditionLogic
 * @notice Evaluates condition trees on scoped function calls.
 * @author gnosisguild
 */
library ConditionLogic {
    function evaluate(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) internal view returns (Result memory) {
        Operator operator = condition.operator;
        if (payload.overflow) {
            return
                Result({
                    status: Status.CalldataOverflow,
                    consumptions: consumptions,
                    info: bytes32(payload.location)
                });
        }

        if (operator < Operator.EqualTo) {
            if (operator == Operator.Pass) {
                return
                    Result({
                        status: Status.Ok,
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator == Operator.Matches) {
                return
                    _matches(data, condition, payload, consumptions, context);
            } else if (operator == Operator.And) {
                return _and(data, condition, payload, consumptions, context);
            } else if (operator == Operator.Or) {
                return _or(data, condition, payload, consumptions, context);
            } else if (operator == Operator.Slice) {
                return _slice(data, condition, payload, consumptions, context);
            } else if (operator == Operator.Pluck) {
                context.pluckedValues[uint8(condition.compValue[0])] = __input(
                    data,
                    payload,
                    context
                );
                return
                    Result({
                        status: Status.Ok,
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator == Operator.Empty) {
                return
                    Result({
                        status: data.length == 0
                            ? Status.Ok
                            : Status.CalldataNotEmpty,
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator == Operator.ArraySome) {
                return
                    _arraySome(data, condition, payload, consumptions, context);
            } else if (operator == Operator.ArrayEvery) {
                return
                    _arrayEvery(
                        data,
                        condition,
                        payload,
                        consumptions,
                        context
                    );
            } else {
                assert(operator == Operator.ArrayTailMatches);
                return
                    _arrayTailMatches(
                        data,
                        condition,
                        payload,
                        consumptions,
                        context
                    );
            }
        } else {
            if (operator <= Operator.LessThan) {
                return
                    Result({
                        status: _compare(data, condition, payload, context),
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator <= Operator.SignedIntLessThan) {
                return
                    Result({
                        status: _compareSignedInt(
                            data,
                            condition,
                            payload,
                            context
                        ),
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator == Operator.WithinAllowance) {
                return
                    __allowance(
                        uint256(__input(data, payload, context)),
                        condition.compValue,
                        Status.AllowanceExceeded,
                        consumptions
                    );
            } else if (operator == Operator.CallWithinAllowance) {
                return
                    __allowance(
                        1,
                        condition.compValue,
                        Status.CallAllowanceExceeded,
                        consumptions
                    );
            } else if (operator == Operator.Bitmask) {
                return
                    Result({
                        status: BitmaskChecker.check(
                            data,
                            condition.compValue,
                            payload
                        ),
                        consumptions: consumptions,
                        info: 0
                    });
            } else if (operator == Operator.WithinRatio) {
                return
                    Result({
                        status: WithinRatioChecker.check(
                            condition.compValue,
                            context.pluckedValues
                        ),
                        consumptions: consumptions,
                        info: 0
                    });
            } else {
                assert(operator == Operator.Custom);
                return _custom(data, condition, payload, consumptions, context);
            }
        }
    }

    function _matches(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        uint256 shift = 32 - condition.compValue.length;
        if (shift < 32) {
            /*
             * Leading Bytes Validation
             *
             * For AbiEncoded + Matches, compValue might contain N bytes that must
             * match the first N bytes of calldata at payload.location.
             *
             * Integrity.sol validates N <= 32, so shift underflow is impossible.
             *
             * Comparison uses right-alignment in bytes32:
             *   shift = 32 - N
             *   expected: bytes32(compValue)  >> shift  (N bytes, right-aligned)
             *   actual:   bytes32(data[loc:]) >> shift  (N bytes, right-aligned)
             *
             * If compValue is empty (shift == 32), validation is skipped.
             */

            bytes32 expected = bytes32(condition.compValue) >> shift;
            bytes32 actual = bytes32(data[payload.location:]) >> shift;

            if (expected != actual) {
                result.status = Status.LeadingBytesNotAMatch;
                return result;
            }
        }

        uint256 sChildCount = condition.sChildCount;
        if (sChildCount != payload.children.length) {
            result.status = Status.ParameterNotAMatch;
            return result;
        }

        Payload memory emptyPayload;
        result.consumptions = consumptions;
        for (uint256 i; i < condition.children.length; ++i) {
            result = evaluate(
                data,
                condition.children[i],
                i < sChildCount ? payload.children[i] : emptyPayload,
                result.consumptions,
                context
            );
            if (result.status != Status.Ok) {
                return result;
            }
        }
        return result;
    }

    function _and(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        Payload memory emptyPayload;
        result.consumptions = consumptions;
        for (uint256 i; i < condition.children.length; ++i) {
            result = evaluate(
                data,
                condition.children[i],
                i < condition.sChildCount
                    ? (payload.variant ? payload.children[i] : payload)
                    : emptyPayload,
                result.consumptions,
                context
            );
            if (result.status != Status.Ok) {
                return result;
            }
        }
        return result;
    }

    function _or(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        Payload memory emptyPayload;
        for (uint256 i; i < condition.children.length; ++i) {
            result = evaluate(
                data,
                condition.children[i],
                i < condition.sChildCount
                    ? (payload.variant ? payload.children[i] : payload)
                    : emptyPayload,
                consumptions,
                context
            );
            if (result.status == Status.Ok) {
                return result;
            }
        }

        return
            Result({
                status: Status.OrViolation,
                consumptions: consumptions,
                info: 0
            });
    }

    /*
     * Slice creates a new Payload that points to a byte-range within the
     * current payload, so the child transparently evaluates against that
     * slice.
     *
     * The child will read the slice via __input, which right-aligns values
     * <=32 bytes to match comparison compValue encoding.
     */
    function _slice(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory) {
        // compValue layout: | 2 bytes: shift | 1 byte: size (1-32)
        uint16 shift = uint16(bytes2(condition.compValue));
        uint8 size = uint8(condition.compValue[2]);

        // Create sliced payload pointing to the slice range
        Payload memory slicedPayload;
        slicedPayload.location =
            payload.location +
            (payload.inlined ? 0 : 32) +
            shift;
        slicedPayload.size = size;

        return
            evaluate(
                data,
                condition.children[0],
                slicedPayload,
                consumptions,
                context
            );
    }

    function _arraySome(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        uint256 length = payload.children.length;
        for (uint256 i; i < length; ++i) {
            result = evaluate(
                data,
                condition.children[0],
                payload.children[i],
                consumptions,
                context
            );
            if (result.status == Status.Ok) {
                return result;
            }
        }
        return
            Result({
                status: Status.NoArrayElementPasses,
                consumptions: consumptions,
                info: 0
            });
    }

    function _arrayEvery(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        result.consumptions = consumptions;
        for (uint256 i; i < payload.children.length; ++i) {
            result = evaluate(
                data,
                condition.children[0],
                payload.children[i],
                result.consumptions,
                context
            );
            if (result.status != Status.Ok) {
                result.status = Status.NotEveryArrayElementPasses;
                return result;
            }
        }
        return result;
    }

    function _arrayTailMatches(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory result) {
        uint256 conditionCount = condition.children.length;
        uint256 childCount = payload.children.length;

        if (childCount < conditionCount) {
            result.status = Status.ParameterNotAMatch;
            return result;
        }

        result.consumptions = consumptions;
        uint256 tailOffset = childCount - conditionCount;

        for (uint256 i; i < conditionCount; ++i) {
            result = evaluate(
                data,
                condition.children[i],
                payload.children[tailOffset + i],
                result.consumptions,
                context
            );
            if (result.status != Status.Ok) {
                return result;
            }
        }
        return result;
    }

    function _compare(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Context memory context
    ) private pure returns (Status) {
        Operator operator = condition.operator;
        // For >32 bytes, compValue is already pre-hashed at storage time
        bytes32 compValue = bytes32(condition.compValue);
        bytes32 value = __input(data, payload, context);

        if (operator == Operator.EqualTo && value != compValue) {
            return Status.ParameterNotAllowed;
        } else if (operator == Operator.GreaterThan && value <= compValue) {
            return Status.ParameterLessThanAllowed;
        } else if (operator == Operator.LessThan && value >= compValue) {
            return Status.ParameterGreaterThanAllowed;
        } else {
            return Status.Ok;
        }
    }

    function _compareSignedInt(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Context memory context
    ) private pure returns (Status) {
        Operator operator = condition.operator;
        int256 compValue = int256(uint256(bytes32(condition.compValue)));
        int256 value = int256(uint256(__input(data, payload, context)));

        if (operator == Operator.SignedIntGreaterThan && value <= compValue) {
            return Status.ParameterLessThanAllowed;
        } else if (
            operator == Operator.SignedIntLessThan && value >= compValue
        ) {
            return Status.ParameterGreaterThanAllowed;
        } else {
            return Status.Ok;
        }
    }

    function _custom(
        bytes calldata data,
        Condition memory condition,
        Payload memory payload,
        Consumption[] memory consumptions,
        Context memory context
    ) private view returns (Result memory) {
        // first 20 bytes: adapter address
        ICustomCondition adapter = ICustomCondition(
            address(bytes20(condition.compValue))
        );

        bytes memory extra;
        if (condition.compValue.length > 20) {
            bytes memory compValue = condition.compValue;
            assembly {
                let len := sub(mload(compValue), 20)
                extra := mload(0x40)
                mstore(0x40, add(extra, add(0x40, len)))
                mstore(extra, len)
                mcopy(add(extra, 0x20), add(compValue, 0x34), len)
            }
        }

        (bool success, bytes32 info) = adapter.check(
            context.to,
            context.value,
            data,
            context.operation,
            payload.location,
            payload.size,
            extra
        );
        return
            Result({
                status: success ? Status.Ok : Status.CustomConditionViolation,
                consumptions: consumptions,
                info: info
            });
    }

    function __allowance(
        uint256 value,
        bytes memory compValue,
        Status failureStatus,
        Consumption[] memory consumptions
    ) private view returns (Result memory) {
        (
            bool success,
            Consumption[] memory nextConsumptions
        ) = WithinAllowanceChecker.check(consumptions, value, compValue);

        return
            Result({
                status: success ? Status.Ok : failureStatus,
                consumptions: nextConsumptions,
                info: bytes32(compValue)
            });
    }

    /**
     * @dev Reads a value from calldata or from Transaction.value, right-aligning when needed.
     *      - payload.size == 0: returns context.value (ether amount)
     *      - payload.size <= 32: reads from calldata and right-aligns
     *      - payload.size > 32: returns keccak256 hash of the calldata slice
     *
     * @param data    The calldata to read from.
     * @param payload The payload specifying location and size.
     * @param context The execution context (for ether value).
     * @return result The value as bytes32, right-aligned or hashed.
     */
    function __input(
        bytes calldata data,
        Payload memory payload,
        Context memory context
    ) private pure returns (bytes32 result) {
        if (payload.size == 32) {
            assembly {
                result := calldataload(add(data.offset, mload(payload)))
            }
            return result;
        }

        uint256 size = payload.size;
        if (size == 0) {
            return bytes32(context.value);
        }

        uint256 location = payload.location;
        if (size < 32) {
            // Right-align
            return bytes32(data[location:]) >> ((32 - size) * 8);
        }

        return keccak256(data[location:location + size]);
    }
}

contracts/core/evaluate/WithinAllowanceC.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../common/AllowanceLoader.sol";
import "../../periphery/interfaces/IPricing.sol";

import {Consumption} from "../../types/Allowance.sol";

/**
 * @title WithinAllowanceChecker
 * @notice Validates allowance consumption with optional price conversion.
 *
 * @dev When a price adapter is used, the spent amount is converted into the
 *      allowance's base denomination. This conversion accounts for two
 *      distinct decimal domains:
 *
 *      • accrueDecimals — decimals of the allowance's base unit
 *      • paramDecimals  — decimals of the asset being spent
 *
 *      This allows multiple assets with different decimal formats to be
 *      aggregated under a single allowance.
 *
 * @author gnosisguild
 */
library WithinAllowanceChecker {
    function check(
        Consumption[] memory consumptions,
        uint256 value,
        bytes memory compValue
    ) internal view returns (bool success, Consumption[] memory) {
        (Consumption memory entry, uint256 index) = _lookup(
            consumptions,
            bytes32(compValue)
        );

        entry.consumed += _convert(value, compValue);

        if (entry.consumed <= entry.balance) {
            return (true, _put(consumptions, index, entry));
        } else {
            return (false, consumptions);
        }
    }

    /// @dev Converts value to base denomination via pricing contract encoded in
    ///      compValue. Returns value unchanged if no pricing configured.
    function _convert(
        uint256 value,
        bytes memory compValue
    ) private view returns (uint128) {
        /**
         * CompValue Layout (32 or 54 bytes):
         * ┌─────────────────────────┬─────────────────────┬──────────┬──────────┐
         * │      allowanceKey       │       adapter       │  accrue  │  param   │
         * │        (bytes32)        │      (address)      │ decimals │ decimals │
         * ├─────────────────────────┼─────────────────────┼──────────┼──────────┤
         * │         0 - 31          │       32 - 51       │    52    │    53    │
         * └─────────────────────────┴─────────────────────┴──────────┴──────────┘
         *                           └─────────────── optional ─────────────────┘
         */
        if (compValue.length > 32) {
            address adapter;
            assembly {
                adapter := shr(96, mload(add(compValue, 0x40)))
            }
            uint8 accrueDecimals = uint8(compValue[52]);
            uint8 paramDecimals = uint8(compValue[53]);

            uint256 price = IPricing(adapter).getPrice();

            /*
             *             value × price     10^accrueDecimals
             *   result = ─────────────── × ───────────────────
             *                 10^18          10^paramDecimals
             */
            value =
                (value * price * (10 ** accrueDecimals)) /
                (10 ** (18 + paramDecimals));
        }

        return uint128(value);
    }

    /// @dev Finds consumption in array by key, or loads from storage if not
    ///      present. Returns a copy
    function _lookup(
        Consumption[] memory consumptions,
        bytes32 allowanceKey
    ) private view returns (Consumption memory consumption, uint256 index) {
        uint256 length = consumptions.length;

        for (; index < length; ++index) {
            if (consumptions[index].allowanceKey == allowanceKey) break;
        }

        if (index < length) {
            consumption = Consumption(
                allowanceKey,
                consumptions[index].balance,
                consumptions[index].consumed,
                consumptions[index].timestamp
            );
        } else {
            (uint128 balance, uint64 timestamp) = AllowanceLoader.accrue(
                allowanceKey,
                uint64(block.timestamp)
            );
            consumption = Consumption(allowanceKey, balance, 0, timestamp);
        }
    }

    /// @dev Shallow copies array, and writes entry at index.
    function _put(
        Consumption[] memory consumptions,
        uint256 index,
        Consumption memory entry
    ) private pure returns (Consumption[] memory result) {
        uint256 prevLength = consumptions.length;
        uint256 length = prevLength + (index == prevLength ? 1 : 0);

        assembly {
            // Load the free memory pointer
            result := mload(0x40)

            // allocate array, by advancing free memory pointer
            mstore(0x40, add(result, mul(add(length, 1), 0x20)))

            // Store the new array length
            mstore(result, length)

            // Copy the previous elements (shallow copy of pointers)
            let dst := add(result, 0x20)
            let src := add(consumptions, 0x20)
            let size := mul(prevLength, 0x20)
            mcopy(dst, src, size)
        }

        result[index] = entry;
    }
}

contracts/core/evaluate/WithinRatioCheck.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../periphery/interfaces/IPricing.sol";

import "../../types/Types.sol";

/**
 * @title WithinRatioChecker
 * @notice Validates that a relative amount falls within a ratio range of a
 *         reference amount, with optional price adapter conversion.
 *
 * @dev Ratio is computed in basis points (10000 = 100%). Both amounts are read
 *      from pluckedValues by index, scaled to shared precision, optionally
 *      converted via price adapters, then compared against [minRatio, maxRatio].
 *
 * @author gnosisguild
 */
library WithinRatioChecker {
    /// @dev 100% = 10000 bps
    uint256 private constant BPS = 10000;

    function check(
        bytes memory compValue,
        bytes32[] memory pluckedValues
    ) internal view returns (Status) {
        CompValue memory unpacked = _unpack(compValue);

        (uint256 referenceAmount, uint256 relativeAmount) = _scaleAndConvert(
            unpacked,
            pluckedValues
        );

        /*
         *                 relativeAmount × priceRel
         *   ratio (bps) = ───────────────────────── × 10,000
         *                 referenceAmount × priceRef
         */
        uint256 ratio = (relativeAmount * BPS) / referenceAmount;

        if (unpacked.minRatio > 0 && ratio < unpacked.minRatio) {
            return Status.RatioBelowMin;
        }
        if (unpacked.maxRatio > 0 && ratio > unpacked.maxRatio) {
            return Status.RatioAboveMax;
        }

        return Status.Ok;
    }

    function _scaleAndConvert(
        CompValue memory unpacked,
        bytes32[] memory pluckedValues
    )
        private
        view
        returns (
            uint256 convertedReferenceAmount,
            uint256 convertedRelativeAmount
        )
    {
        // Calculate precision
        uint256 precision = unpacked.referenceDecimals >
            unpacked.relativeDecimals
            ? unpacked.referenceDecimals
            : unpacked.relativeDecimals;
        precision = precision > 18 ? precision : 18;

        // Extract and scale amounts from plucked values
        uint256 referenceAmount = uint256(
            pluckedValues[unpacked.referenceIndex]
        ) * (10 ** (precision - unpacked.referenceDecimals));

        uint256 relativeAmount = uint256(
            pluckedValues[unpacked.relativeIndex]
        ) * (10 ** (precision - unpacked.relativeDecimals));

        /*
         * Get prices from adapters (address(0) = disabled = 1:1)
         *
         * Examples:
         *   - Cross-asset (BTC vs ETH via USD):
         *       refAdapter = ETH/USD, relAdapter = BTC/USD
         *   - Single adapter (WBTC vs ETH, base = ETH):
         *       refAdapter = 0, relAdapter = WBTC/ETH
         */
        uint256 priceScale = 10 ** (precision - 18);
        uint256 priceReference = unpacked.referenceAdapter != address(0)
            ? IPricing(unpacked.referenceAdapter).getPrice() * priceScale
            : (10 ** precision);

        uint256 priceRelative = unpacked.relativeAdapter != address(0)
            ? IPricing(unpacked.relativeAdapter).getPrice() * priceScale
            : (10 ** precision);

        // Convert to common base
        uint256 denominator = 10 ** precision;
        convertedReferenceAmount =
            (referenceAmount * priceReference) /
            denominator;
        convertedRelativeAmount =
            (relativeAmount * priceRelative) /
            denominator;
    }

    function _unpack(
        bytes memory compValue
    ) private pure returns (CompValue memory unpacked) {
        /**
         * CompValue Layout (12 or 52 bytes):
         * ┌─────────┬─────────┬─────────┬─────────┬──────────┬──────────┬─────────────────┬─────────────────┐
         * │  refIdx │ refDec  │ relIdx  │ relDec  │ minRatio │ maxRatio │   refAdapter    │   relAdapter    │
         * │ (uint8) │ (uint8) │ (uint8) │ (uint8) │ (uint32) │ (uint32) │    (address)    │    (address)    │
         * ├─────────┼─────────┼─────────┼─────────┼──────────┼──────────┼─────────────────┼─────────────────┤
         * │    0    │    1    │    2    │    3    │   4–7    │   8–11   │     12–31       │     32–51       │
         * └─────────┴─────────┴─────────┴─────────┴──────────┴──────────┴─────────────────┴─────────────────┘
         *                                                               └────────────── optional ──────────┘
         */
        bytes32 packedBytes32 = bytes32(compValue);

        unpacked.referenceIndex = uint8(bytes1(packedBytes32));
        unpacked.referenceDecimals = uint8(bytes1(packedBytes32 << 8));
        unpacked.relativeIndex = uint8(bytes1(packedBytes32 << 16));
        unpacked.relativeDecimals = uint8(bytes1(packedBytes32 << 24));
        unpacked.minRatio = uint32(bytes4(packedBytes32 << 32));
        unpacked.maxRatio = uint32(bytes4(packedBytes32 << 64));
        unpacked.referenceAdapter = address(bytes20(packedBytes32 << 96));
        if (compValue.length > 32) {
            assembly {
                mstore(
                    add(unpacked, 0xe0),
                    shr(96, mload(add(compValue, 0x40)))
                )
            }
        }
    }

    struct CompValue {
        uint8 referenceIndex;
        uint8 referenceDecimals;
        uint8 relativeIndex;
        uint8 relativeDecimals;
        uint32 minRatio;
        uint32 maxRatio;
        address referenceAdapter;
        address relativeAdapter;
    }
}

contracts/core/Membership.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Storage.sol";

/**
 * @title   Membership
 *
 * @notice  Validates that a module holds an active role membership before
 *          authorizing transactions. Memberships can be time-bound and usage-limited.
 *
 * @dev     moduleOnly reverts if the call is not sent or signed by an enabled module.
 *          Membership packs timestamps and uses into a single uint256. See layout below.
 *
 * @author  gnosisguild
 */

abstract contract Membership is RolesStorage {
    uint256 private constant _MEMBERSHIP_NOOP = type(uint256).max;
    uint256 private constant _MEMBERSHIP_REVOKE = 0;

    function _authenticate(
        bytes32 roleKey
    ) internal moduleOnly returns (address module, uint256 nextMembership) {
        // Never authorize the zero role
        if (roleKey == 0) {
            revert NoMembership();
        }

        module = sentOrSignedByModule();

        uint256 membership = roles[roleKey].members[module];
        if (membership == 0) {
            revert NoMembership();
        }

        /*
         * Membership Layout (256 bits)
         * ┌────────────────┬────────────────┬─────────────────────────────────┐
         * │ startTimestamp │  endTimestamp  │            usesLeft             │
         * │    64 bits     │    64 bits     │            128 bits             │
         * └────────────────┴────────────────┴─────────────────────────────────┘
         *
         * - startTimestamp: Unix timestamp when membership becomes valid
         * - endTimestamp:   Unix timestamp when membership expires
         * - usesLeft:       Remaining uses (type(uint128).max = unlimited)
         */

        if (block.timestamp < uint64(membership >> 192)) {
            revert MembershipNotYetValid();
        }
        if (block.timestamp > uint64(membership >> 128)) {
            revert MembershipExpired();
        }
        if (~membership << 128 == 0) {
            // unlimited uses
            return (module, _MEMBERSHIP_NOOP);
        }

        uint256 usesLeft = (membership << 128) >> 128;

        return (
            module,
            usesLeft <= 1
                ? _MEMBERSHIP_REVOKE
                : ((membership >> 128) << 128) | (usesLeft - 1)
        );
    }
}

contracts/core/serialize/ConditionPacker.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../types/Types.sol";

/**
 * ScopedFunction Layout in Contract Storage
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ HEADER (3 bytes)                                                    │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ • layoutOffset             16 bits (0-65535)                        │
 * │ • maxPluckIndex             8 bits (0-255)                          │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ CONDITION TREE (at conditionTreeOffset)                             │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Header (2 bytes):                                                   │
 * │   • nodeCount              16 bits (0-65535)                        │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Nodes (nodeCount × 5 bytes each):                                   │
 * │   Each node (40 bits):                                              │
 * │     • operator              5 bits                                  │
 * │     • unused                3 bits                                  │
 * │     • childCount            8 bits  (0-255)                         │
 * │     • sChildCount           8 bits  (0-255)                         │
 * │     • compValueOffset      16 bits  (0 if no value)                 │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ CompValues (variable length):                                       │
 * │   For each condition with operator >= EqualTo:                      │
 * │     • length               16 bits  (compValue byte length)         │
 * │     • data                 N bytes  (actual compValue data)         │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ LAYOUT TREE (at layoutOffset)                                       │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Header (2 bytes):                                                   │
 * │   • nodeCount              16 bits (0-65535)                        │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Nodes (nodeCount × 3 bytes each):                                   │
 * │   Each node (24 bits = 3 bytes):                                    │
 * │     • encoding              3 bits                                  │
 * │     • inlined               1 bit                                   │
 * │     • childCount            8 bits                                  │
 * │     • leadingBytes         12 bits                                  │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * NOTES:
 * - All offsets are relative to the start of the buffer
 * - Offsets are in bytes
 * - Children are implicitly sequential in BFS order (no childrenStart needed)
 */

library ConditionPacker {
    // ═══════════════════════════════════════════════════════════════════════════
    // Constants
    // ═══════════════════════════════════════════════════════════════════════════

    uint256 private constant CONDITION_NODE_BYTES = 5;
    uint256 private constant LAYOUT_NODE_BYTES = 3;

    // ═══════════════════════════════════════════════════════════════════════════
    // Main Packing Function
    // ═══════════════════════════════════════════════════════════════════════════

    function pack(
        ConditionFlat[] memory conditions,
        Layout memory layout
    ) internal pure returns (bytes memory buffer) {
        uint256 layoutOffset = 3 + _conditionPackedSize(conditions);

        buffer = new bytes(layoutOffset + _layoutPackedSize(layout));

        _packConditions(conditions, buffer, 3);
        _packLayout(layout, buffer, layoutOffset);

        // Header: layoutOffset (2 bytes) + maxPluckIndex (1 byte)
        buffer[0] = bytes1(uint8(layoutOffset >> 8));
        buffer[1] = bytes1(uint8(layoutOffset));
        buffer[2] = bytes1(_maxPluckIndex(conditions));
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Condition Packing
    // ═══════════════════════════════════════════════════════════════════════════

    function _conditionPackedSize(
        ConditionFlat[] memory conditions
    ) private pure returns (uint256 result) {
        uint256 count = conditions.length;

        // Header (2 bytes) + nodes
        result = 2 + count * CONDITION_NODE_BYTES;

        // Add space for compValues
        for (uint256 i; i < count; ++i) {
            if (!_hasCompValue(conditions[i])) continue;

            // 2 bytes for length field
            result += 2;

            if (conditions[i].paramType == Encoding.AbiEncoded) {
                result += conditions[i].compValue.length - 2; // skip leadingBytes
            } else if (
                conditions[i].operator == Operator.EqualTo &&
                conditions[i].compValue.length > 32
            ) {
                result += 32; // store keccak256 hash
            } else {
                result += conditions[i].compValue.length;
            }
        }
    }

    function _packConditions(
        ConditionFlat[] memory conditions,
        bytes memory buffer,
        uint256 offset
    ) private pure {
        offset += _packUInt16(conditions.length, buffer, offset);

        uint256 tailOffset = offset +
            (conditions.length * CONDITION_NODE_BYTES);

        for (uint256 i; i < conditions.length; ++i) {
            (, uint256 childCount, uint256 sChildCount) = _childBounds(
                conditions,
                i
            );

            ConditionFlat memory condition = conditions[i];
            bool hasCompValue = _hasCompValue(condition);

            // Pack Node
            {
                // byte 1: operator (5 bits) shifted left, 3 trailing unused bits
                buffer[offset++] = bytes1(uint8(conditions[i].operator) << 3);
                // byte 2
                buffer[offset++] = bytes1(uint8(childCount));
                // byte 3
                buffer[offset++] = bytes1(uint8(sChildCount));

                // bytes 4-5: compValueOffset (0 if no compValue)
                buffer[offset++] = hasCompValue
                    ? bytes1(uint8(tailOffset >> 8))
                    : bytes1(0);
                buffer[offset++] = hasCompValue
                    ? bytes1(uint8(tailOffset))
                    : bytes1(0);

                // todo encode a test with a high number of compValues, make sure both bytes on the tailOffset are considered
            }

            if (!hasCompValue) continue;

            // Pack CompValue - three cases:
            // 1. AbiEncoded match bytes: store N bytes (skip first 2 bytes of compValue)
            // 2. EqualTo with >32 bytes: store keccak256 hash (32 bytes)
            // 3. Other comparisons: store compValue as-is
            bytes memory compValue = condition.compValue;
            uint256 length;
            uint256 srcOffset;
            if (condition.paramType == Encoding.AbiEncoded) {
                length = compValue.length - 2;
                srcOffset = 2; // skip leadingBytes
            } else if (
                condition.operator == Operator.EqualTo && compValue.length > 32
            ) {
                compValue = abi.encodePacked(keccak256(compValue));
                length = 32;
                srcOffset = 0;
            } else {
                length = compValue.length;
                srcOffset = 0;
            }

            buffer[tailOffset++] = bytes1(uint8(length >> 8));
            buffer[tailOffset++] = bytes1(uint8(length));

            assembly {
                let src := add(add(0x20, compValue), srcOffset)
                let dst := add(add(0x20, buffer), tailOffset)
                mcopy(dst, src, length)
            }
            tailOffset += length;
        }
    }

    function _hasCompValue(
        ConditionFlat memory condition
    ) private pure returns (bool) {
        return
            condition.operator >= Operator.EqualTo ||
            condition.operator == Operator.Slice ||
            condition.operator == Operator.Pluck ||
            (condition.paramType == Encoding.AbiEncoded &&
                condition.compValue.length > 2);
    }

    function _maxPluckIndex(
        ConditionFlat[] memory conditions
    ) private pure returns (uint8 result) {
        for (uint256 i = 0; i < conditions.length; ++i) {
            if (conditions[i].operator != Operator.Pluck) continue;

            uint8 index = uint8(conditions[i].compValue[0]);
            if (index > result) {
                result = index;
            }
        }
    }

    function _childBounds(
        ConditionFlat[] memory conditions,
        uint256 index
    )
        private
        pure
        returns (uint256 childStart, uint256 childCount, uint256 sChildCount)
    {
        for (uint256 i = index + 1; i < conditions.length; ++i) {
            uint256 parent = conditions[i].parent;

            if (parent == index) {
                if (childCount == 0) childStart = i;
                ++childCount;
            } else if (parent > index) {
                break;
            }
        }

        sChildCount = childCount;
        for (uint256 i = childStart + childCount; i > childStart; --i) {
            if (_isNonStructural(conditions, i - 1)) {
                // non structural come last
                --sChildCount;
            } else {
                // break once structural, all the rest will also be
                break;
            }
        }
    }

    function _isNonStructural(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        // NonStructural if paramType is None and all descendants are None
        if (conditions[index].paramType != Encoding.None) {
            return false;
        }

        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index) {
                if (!_isNonStructural(conditions, i)) {
                    return false;
                }
            } else if (conditions[i].parent > index) {
                break;
            }
        }

        return true;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Layout Packing
    // ═══════════════════════════════════════════════════════════════════════════

    function _layoutPackedSize(
        Layout memory node
    ) private pure returns (uint256 result) {
        // Header (2 bytes) + all nodes (3 bytes each)
        result = 2 + _countNodes(node) * LAYOUT_NODE_BYTES;
    }

    function _packLayout(
        Layout memory tree,
        bytes memory buffer,
        uint256 offset
    ) private pure returns (uint256) {
        LayoutNodeFlat[] memory nodes = _flattenLayout(tree);

        offset += _packUInt16(nodes.length, buffer, offset);

        // Pack all nodes (3 bytes each)
        for (uint256 i; i < nodes.length; ++i) {
            uint24 packed = (uint24(nodes[i].encoding) << 21) |
                (nodes[i].inlined ? uint24(1 << 20) : uint24(0)) |
                (uint24(nodes[i].childCount) << 12) |
                uint24(nodes[i].leadingBytes);

            buffer[offset++] = bytes1(uint8(packed >> 16));
            buffer[offset++] = bytes1(uint8(packed >> 8));
            buffer[offset++] = bytes1(uint8(packed));
        }

        return 2 + nodes.length * LAYOUT_NODE_BYTES;
    }

    function _flattenLayout(
        Layout memory root
    ) internal pure returns (LayoutNodeFlat[] memory) {
        uint256 total = _countNodes(root);
        LayoutNodeFlat[] memory result = new LayoutNodeFlat[](total);

        Layout[] memory queue = new Layout[](total);
        uint256[] memory parents = new uint256[](total);

        queue[0] = root;
        parents[0] = 0;

        uint256 head = 0;
        uint256 tail = 1;
        uint256 current = 0;

        while (head < tail) {
            Layout memory node = queue[head];
            uint256 parent = parents[head];
            head++;

            // Record this node
            result[current] = LayoutNodeFlat({
                encoding: node.encoding,
                parent: parent,
                childCount: node.children.length,
                leadingBytes: uint16(node.leadingBytes),
                inlined: node.inlined
            });

            // Enqueue children
            for (uint256 i = 0; i < node.children.length; ++i) {
                queue[tail] = node.children[i];
                parents[tail] = current;
                tail++;
            }

            current++;
        }

        return result;
    }

    function _countNodes(
        Layout memory node
    ) internal pure returns (uint256 count) {
        count = 1;
        for (uint256 i = 0; i < node.children.length; ++i) {
            count += _countNodes(node.children[i]);
        }
    }

    struct LayoutNodeFlat {
        uint256 parent;
        Encoding encoding;
        uint256 childCount;
        uint16 leadingBytes;
        bool inlined;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Buffer Helpers
    // ═══════════════════════════════════════════════════════════════════════════

    function _packUInt16(
        uint256 value,
        bytes memory buffer,
        uint256 offset
    ) private pure returns (uint256) {
        assert(value < type(uint16).max);

        buffer[offset] = bytes1(uint8((value >> 8)));
        buffer[offset + 1] = bytes1(uint8((value)));

        return 2;
    }
}

contracts/core/serialize/ConditionsTrans.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../common/ImmutableStorage.sol";

import "./ConditionPacker.sol";
import "./Integrity.sol";
import "./TypeTree.sol";

import "../../types/Types.sol";

/**
 * @title ConditionsTransform
 * @notice Validates, transforms, and stores condition trees as immutable bytecode
 *         that can later be loaded without additional overhead.
 * @author gnosisguild
 */
library ConditionsTransform {
    /**
     * @notice Ensures a breadth-first condition tree is well-formed, packs it
     *         and stores the packed bytes as immutable bytecode.
     *
     * @param conditions The flat condition array in BFS order.
     * @return pointer Address of the contract where the packed conditions are stored.
     */
    function packAndStore(
        ConditionFlat[] memory conditions
    ) external returns (address) {
        Integrity.enforce(conditions);

        _patchEtherValue(conditions);
        _removeExtraneousOffsets(conditions);

        Layout memory layout;
        // For non-structural trees skip TypeTree creation
        if (Topology.isStructural(conditions, 0)) {
            layout = TypeTree.inspect(conditions, 0);
        }

        bytes memory buffer = ConditionPacker.pack(conditions, layout);
        address pointer = ImmutableStorage.store(buffer);

        return pointer;
    }

    /**
     * @dev Patches EtherValue encoding to None after Integrity validation.
     *      This enables downstream detection via payload.size == 0.
     */
    function _patchEtherValue(ConditionFlat[] memory conditions) private pure {
        uint256 count = conditions.length;
        for (uint256 i; i < count; ++i) {
            if (conditions[i].paramType == Encoding.EtherValue) {
                conditions[i].paramType = Encoding.None;
            }
        }
    }

    /**
     * @dev Removes unnecessary offsets from compValue fields of the conditions
     * array. Its purpose is to ensure a consistent API where every `compValue`
     * provided for use in `Operations.EqualTo` is obtained by calling
     * `abi.encode` directly.
     *
     * By removing the leading extraneous offsets this function makes
     * `abi.encode(...)` output line up with the layout produced by Decoder
     * inspection. Without it, callers would need to patch compValues
     * based on whether the payload is fully inline or at offset.
     *
     * @param conditions Array of ConditionFlat structs to normalize
     */
    function _removeExtraneousOffsets(
        ConditionFlat[] memory conditions
    ) private pure {
        uint256 count = conditions.length;
        for (uint256 i; i < count; ++i) {
            if (
                conditions[i].operator == Operator.EqualTo &&
                !_isInline(conditions, i)
            ) {
                bytes memory compValue = conditions[i].compValue;
                uint256 length = compValue.length;
                assembly {
                    compValue := add(compValue, 32)
                    mstore(compValue, sub(length, 32))
                }
                conditions[i].compValue = compValue;
            }
        }
    }

    /**
     * @dev Returns true when the encoded parameter is stored inline (no ABI
     *      offset), recursing through tuple children to ensure every leaf is
     *      inline as well.
     */
    function _isInline(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        Encoding paramType = conditions[index].paramType;
        if (paramType == Encoding.Static) {
            return true;
        } else if (
            paramType == Encoding.Dynamic ||
            paramType == Encoding.Array ||
            paramType == Encoding.AbiEncoded
        ) {
            return false;
        } else {
            // Tuple: check all children recursively
            uint256 length = conditions.length;
            for (uint256 j = index + 1; j < length; ++j) {
                uint8 parent = conditions[j].parent;
                if (parent < index) {
                    continue;
                }

                if (parent > index) {
                    break;
                }

                if (!_isInline(conditions, j)) {
                    return false;
                }
            }
            return true;
        }
    }
}

contracts/core/serialize/ConditionUnpack.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../types/Types.sol";

library ConditionUnpacker {
    // ═══════════════════════════════════════════════════════════════════════════
    // Constants
    // ═══════════════════════════════════════════════════════════════════════════

    uint256 private constant CONDITION_NODE_BYTES = 5;
    uint256 private constant LAYOUT_NODE_BYTES = 3;

    // ═══════════════════════════════════════════════════════════════════════════
    // Main Unpacking Function
    // ═══════════════════════════════════════════════════════════════════════════

    function unpack(
        bytes memory buffer
    )
        internal
        view
        returns (
            Condition memory condition,
            Layout memory layout,
            uint256 maxPluckIndex
        )
    {
        // Unpack condition tree (starts at offset 3)
        condition = _unpackCondition(buffer, 3);

        // Unpack layout
        layout = _unpackLayout(buffer, uint16(bytes2(buffer)));

        // Read maxPluckIndex from header
        maxPluckIndex = uint8(buffer[2]);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Condition Unpacking
    // ═══════════════════════════════════════════════════════════════════════════

    function _unpackCondition(
        bytes memory buffer,
        uint256 offset
    ) private view returns (Condition memory) {
        // Load the node count from header (16 bits)
        uint256 nodeCount;
        assembly {
            offset := add(0x20, add(buffer, offset))
            nodeCount := shr(240, mload(offset))
            offset := add(offset, 2)
        }

        Condition[] memory nodes = new Condition[](nodeCount);
        uint256 nextChildBFS = 1;

        for (uint256 i = 0; i < nodeCount; ++i) {
            uint256 packed;
            assembly {
                packed := shr(216, mload(offset)) // Load 5 bytes, 40 bits
            }

            /*
             * ┌───────────────────────────────────────────────────────────┐
             * │Each node 40 bits, 5 bytes:                                │
             * │  • operator              5 bits                           │
             * │  • unused                3 bits                           │
             * │  • childCount            8 bits  (0-255)                  │
             * │  • sChildCount           8 bits  (0-255)                  │
             * │  • compValueOffset      16 bits  (0 if no value)          │
             * └───────────────────────────────────────────────────────────┘
             */

            Condition memory node = nodes[i];
            node.operator = Operator((packed >> 35) & 0x1F);
            uint256 childCount = (packed >> 24) & 0xFF;
            node.sChildCount = (packed >> 16) & 0xFF;
            uint256 offsetCompValue = packed & 0xFFFF;

            if (offsetCompValue != 0) {
                uint256 length;
                assembly {
                    // load 2 bytes, 16 bits
                    length := shr(
                        240,
                        mload(add(0x20, add(buffer, offsetCompValue)))
                    )
                }

                bytes memory compValue = new bytes(length);
                assembly {
                    let src := add(0x22, add(buffer, offsetCompValue))
                    let dst := add(0x20, compValue)
                    mcopy(dst, src, length)
                }
                node.compValue = compValue;
            }

            if (childCount > 0) {
                node.children = new Condition[](childCount);
                for (uint256 j = 0; j < childCount; ++j) {
                    node.children[j] = nodes[nextChildBFS];
                    unchecked {
                        ++nextChildBFS;
                    }
                }
            } else if (node.operator == Operator.EqualToAvatar) {
                node.operator = Operator.EqualTo;
                node.compValue = abi.encode(_avatar());
            }

            unchecked {
                offset += CONDITION_NODE_BYTES;
            }
        }

        // Return root node
        return nodes[0];
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Layout Unpacking
    // ═══════════════════════════════════════════════════════════════════════════

    function _unpackLayout(
        bytes memory buffer,
        uint256 offset
    ) private pure returns (Layout memory) {
        // Load the node count from header (16 bits)
        uint256 nodeCount;
        assembly {
            offset := add(0x20, add(buffer, offset))
            nodeCount := shr(240, mload(offset))
            offset := add(offset, 2)
        }

        Layout[] memory nodes = new Layout[](nodeCount);
        uint256 nextChildBFS = 1;

        for (uint256 i = 0; i < nodeCount; ++i) {
            uint256 packed;
            assembly {
                packed := shr(232, mload(offset)) // Load 3 bytes (24 bits)
            }
            /*
             * ┌──────────────────────────────────────────────────────────┐
             * │Each node (24 bits = 3 bytes):                            │
             * │  • encoding              3 bits  (bits 23-21)            │
             * │  • inlined               1 bit   (bit 20)                │
             * │  • childCount            8 bits  (bits 19-12)            │
             * │  • leadingBytes         12 bits  (bits 11-0)             │
             * └──────────────────────────────────────────────────────────┘
             */

            Layout memory node = nodes[i];
            node.encoding = Encoding((packed >> 21) & 0x07);
            node.inlined = (packed >> 20) & 1 != 0;
            uint256 childCount = (packed >> 12) & 0xFF;
            node.leadingBytes = packed & 0xFFF;

            if (childCount > 0) {
                node.children = new Layout[](childCount);

                for (uint256 j = 0; j < childCount; ++j) {
                    node.children[j] = nodes[nextChildBFS];
                    unchecked {
                        ++nextChildBFS;
                    }
                }
            }

            unchecked {
                offset += LAYOUT_NODE_BYTES;
            }
        }

        return nodes[0];
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Helper
    // ═══════════════════════════════════════════════════════════════════════════

    function _avatar() private view returns (address result) {
        assembly {
            result := sload(101)
        }
    }
}

contracts/core/serialize/Integrity.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Topology.sol";
import "./TypeTree.sol";

import {IRolesError} from "../../types/RolesError.sol";

/**
 * @title Integrity, A library that validates condition integrity, and
 * adherence to the expected input structure and rules.
 *
 * @author gnosisguild
 */
library Integrity {
    function enforce(ConditionFlat[] memory conditions) internal pure {
        _root(conditions);
        _bfs(conditions);
        for (uint256 i = 0; i < conditions.length; ++i) {
            _node(conditions[i], i);
        }
        _parents(conditions);
        _children(conditions);
        _nonStructuralOrdering(conditions);
        _typeTree(conditions);
        _pluckOrder(conditions, 0, 0);
    }

    function _root(ConditionFlat[] memory conditions) private pure {
        uint256 count;

        for (uint256 i; i < conditions.length; ++i) {
            if (conditions[i].parent == i) ++count;
        }
        if (count != 1 || conditions[0].parent != 0) {
            revert IRolesError.UnsuitableRootNode();
        }
    }

    function _bfs(ConditionFlat[] memory conditions) private pure {
        uint256 length = conditions.length;
        // check BFS
        for (uint256 i = 1; i < length; ++i) {
            if (conditions[i - 1].parent > conditions[i].parent) {
                revert IRolesError.NotBFS();
            }

            if (conditions[i].parent > i) {
                revert IRolesError.NotBFS();
            }
        }
    }

    function _node(ConditionFlat memory condition, uint256 index) private pure {
        Operator operator = condition.operator;
        Encoding encoding = condition.paramType;
        bytes memory compValue = condition.compValue;
        if (operator == Operator.Pass) {
            if (condition.compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.And || operator == Operator.Or) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (condition.compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Empty) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Matches) {
            if (
                encoding != Encoding.Tuple &&
                encoding != Encoding.Array &&
                encoding != Encoding.AbiEncoded
            ) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // For AbiEncoded, compValue must be:
            //    - empty (leadingBytes defaults to 4)
            //    - 2 bytes (leadingBytes, any offset)
            //    - 2 + N bytes (leadingBytes + N match bytes, N <= 32)
            // For Tuple/Array: compValue must be empty
            if (encoding == Encoding.AbiEncoded) {
                uint16 leadingBytes = compValue.length >= 2
                    ? uint16(bytes2(compValue))
                    : 0;
                bool valid = compValue.length == 0 ||
                    compValue.length == 2 ||
                    (compValue.length == 2 + leadingBytes &&
                        leadingBytes <= 32);

                if (!valid) {
                    revert IRolesError.UnsuitableCompValue(index);
                }
            } else if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (
            operator == Operator.ArraySome || operator == Operator.ArrayEvery
        ) {
            if (encoding != Encoding.Array) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.ArrayTailMatches) {
            if (encoding != Encoding.Array) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Slice) {
            // Slice can be applied to Static or Dynamic parameters
            if (encoding != Encoding.Static && encoding != Encoding.Dynamic) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue must be 3 bytes: 2 bytes shift + 1 byte size (1-32)
            if (compValue.length != 3) {
                revert IRolesError.UnsuitableCompValue(index);
            }
            uint8 size = uint8(compValue[2]);
            if (size == 0 || size > 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Pluck) {
            // Pluck must be Static or EtherValue
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue is the index (1 byte, 0-255)
            if (compValue.length != 1) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.EqualToAvatar) {
            if (encoding != Encoding.Static) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.EqualTo) {
            if (
                encoding != Encoding.Static &&
                encoding != Encoding.Dynamic &&
                encoding != Encoding.Tuple &&
                encoding != Encoding.Array &&
                encoding != Encoding.EtherValue
            ) {
                revert IRolesError.UnsuitableParameterType(index);
            }

            bool unsuitable;
            if (_isWordLike(encoding)) {
                unsuitable = compValue.length != 32;
            } else if (encoding == Encoding.Dynamic) {
                unsuitable = compValue.length == 0;
            } else {
                unsuitable = compValue.length < 32;
            }

            if (unsuitable) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (
            operator == Operator.GreaterThan ||
            operator == Operator.LessThan ||
            operator == Operator.SignedIntGreaterThan ||
            operator == Operator.SignedIntLessThan
        ) {
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Bitmask) {
            if (encoding != Encoding.Static && encoding != Encoding.Dynamic) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue layout: | 2 bytes: shift | N bytes: mask | N bytes: expected |
            if (compValue.length < 4 || (compValue.length - 2) % 2 != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Custom) {
            if (compValue.length < 20) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.WithinAllowance) {
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // 32 bytes: allowanceKey only (legacy)
            // 54 bytes: allowanceKey + adapter + accrueDecimals + paramDecimals
            if (compValue.length != 32 && compValue.length != 54) {
                revert IRolesError.UnsuitableCompValue(index);
            }
            if (compValue.length == 54) {
                if (uint8(compValue[52]) > 18 || uint8(compValue[53]) > 18) {
                    revert IRolesError.AllowanceDecimalsExceedMax(index);
                }
            }
        } else if (operator == Operator.CallWithinAllowance) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // CallWithinAllowance always uses value=1, price adapter doesn't make sense
            if (compValue.length != 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.WithinRatio) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length < 32 || compValue.length > 52) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else {
            revert IRolesError.UnsupportedOperator(index);
        }
    }

    function _parents(ConditionFlat[] memory conditions) private pure {
        uint256 length = conditions.length;

        for (uint256 i = 0; i < length; ++i) {
            if (conditions[i].operator == Operator.CallWithinAllowance) {
                if (conditions[i].parent != i) {
                    (, , uint256 countOtherNodes) = _countParentNodes(
                        conditions,
                        i
                    );

                    if (countOtherNodes > 0) {
                        revert IRolesError.UnsuitableParent(i);
                    }
                }
            } else if (conditions[i].operator == Operator.Empty) {
                if (conditions[i].parent != i) {
                    (
                        uint256 countAbiEncoded,
                        ,
                        uint256 countOtherNodes
                    ) = _countParentNodes(conditions, i);

                    if (countAbiEncoded > 0 || countOtherNodes > 0) {
                        revert IRolesError.UnsuitableParent(i);
                    }
                }
            } else if (conditions[i].operator == Operator.WithinRatio) {
                // WithinRatio can be anywhere except as the root node
                if (conditions[i].parent == i) {
                    revert IRolesError.UnsuitableParent(i);
                }
            }
        }
    }

    function _children(ConditionFlat[] memory conditions) private pure {
        for (uint256 i = 0; i < conditions.length; i++) {
            ConditionFlat memory condition = conditions[i];
            (, uint256 childCount, uint256 sChildCount) = Topology.childBounds(
                conditions,
                i
            );

            if (condition.paramType == Encoding.None) {
                if (
                    (condition.operator == Operator.CallWithinAllowance ||
                        condition.operator == Operator.WithinRatio ||
                        condition.operator == Operator.Empty) && childCount != 0
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
                if (
                    condition.operator == Operator.And ||
                    condition.operator == Operator.Or
                ) {
                    if (childCount == 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Static) {
                // Slice can have at most one child; other Static operators have no children
                if (condition.operator == Operator.Slice) {
                    if (childCount > 1) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                } else {
                    if (childCount != 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Dynamic) {
                // Slice can have at most one child; other Dynamic operators have no children
                if (condition.operator == Operator.Slice) {
                    if (childCount > 1) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                } else {
                    if (childCount != 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Tuple) {
                if (sChildCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else if (condition.paramType == Encoding.AbiEncoded) {
                if (childCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else if (condition.paramType == Encoding.Array) {
                if (sChildCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
                // Enforce only structural children for array iteration operators
                if (
                    (condition.operator == Operator.ArraySome ||
                        condition.operator == Operator.ArrayEvery ||
                        condition.operator == Operator.ArrayTailMatches) &&
                    childCount != sChildCount
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }

                if (
                    (condition.operator == Operator.ArraySome ||
                        condition.operator == Operator.ArrayEvery) &&
                    childCount != 1
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else {
                assert(condition.paramType == Encoding.EtherValue);
                // EtherValue is non-structural, no children allowed
                if (childCount != 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            }
        }
    }

    function _nonStructuralOrdering(
        ConditionFlat[] memory conditions
    ) private pure {
        for (uint256 i = 0; i < conditions.length; ++i) {
            (uint256 childStart, uint256 childCount, ) = Topology.childBounds(
                conditions,
                i
            );

            if (childCount == 0) continue;

            // Once we see a non-structural child, all remaining must be non-structural
            bool seenNonStructural = false;
            for (uint256 j = 0; j < childCount; j++) {
                uint256 childIndex = childStart + j;
                bool isStructural = Topology.isStructural(
                    conditions,
                    childIndex
                );

                if (seenNonStructural && isStructural) {
                    // Structural child found after non-structural child
                    revert IRolesError.NonStructuralChildrenMustComeLast(i);
                }

                if (!isStructural) {
                    seenNonStructural = true;
                }
            }
        }
    }

    function _typeTree(ConditionFlat[] memory conditions) private pure {
        for (uint256 i = 0; i < conditions.length; ++i) {
            ConditionFlat memory condition = conditions[i];
            if (
                condition.operator == Operator.And ||
                condition.operator == Operator.Or ||
                condition.paramType == Encoding.Array
            ) {
                if (
                    !_isTypeMatch(conditions, i) &&
                    !_isTypeEquivalence(conditions, i)
                ) {
                    revert IRolesError.UnsuitableChildTypeTree(i);
                }
            }

            _slice(conditions, i);
            _withinRatio(conditions, i);
        }
    }

    function _isTypeMatch(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        if (sChildCount == 1) {
            return true;
        }

        bytes32 id = TypeTree.id(conditions, childStart);
        for (uint256 i = 1; i < sChildCount; ++i) {
            if (id != TypeTree.id(conditions, childStart + i)) return false;
        }

        return true;
    }
    function _isTypeEquivalence(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        for (uint256 i = 0; i < sChildCount; ++i) {
            Encoding encoding = TypeTree
                .inspect(conditions, childStart + i)
                .encoding;
            if (
                encoding != Encoding.Dynamic && encoding != Encoding.AbiEncoded
            ) {
                return false;
            }
        }
        return true;
    }

    function _countParentNodes(
        ConditionFlat[] memory conditions,
        uint256 i
    )
        private
        pure
        returns (
            uint256 countAbiEncoded,
            uint256 countLogical,
            uint256 countOther
        )
    {
        for (uint256 j = conditions[i].parent; ; ) {
            bool isAbiEncoded = conditions[j].paramType == Encoding.AbiEncoded;
            bool isLogical = (conditions[j].operator == Operator.And ||
                conditions[j].operator == Operator.Or);

            if (isAbiEncoded) {
                ++countAbiEncoded;
            }

            if (isLogical) {
                ++countLogical;
            }

            if (!isAbiEncoded && !isLogical) {
                ++countOther;
            }

            if (j == 0) {
                break;
            }

            j = conditions[j].parent;
        }
    }

    function _slice(ConditionFlat[] memory conditions, uint256 i) private pure {
        if (conditions[i].operator != Operator.Slice) {
            return;
        }

        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            i
        );

        // Slice must have exactly one child that resolves to Static
        if (sChildCount == 0) {
            revert IRolesError.SliceChildNotStatic(i);
        }

        Layout memory layout = TypeTree.inspect(conditions, childStart);
        if (layout.encoding != Encoding.Static) {
            revert IRolesError.SliceChildNotStatic(i);
        }
    }

    /**
     * @notice Validates WithinRatio operator constraints
     * @dev Checks that at least one ratio bound (min or max) is provided
     */
    function _withinRatio(
        ConditionFlat[] memory conditions,
        uint256 i
    ) private pure {
        if (conditions[i].operator != Operator.WithinRatio) {
            return;
        }

        bytes memory compValue = conditions[i].compValue;

        // Layout: referenceIndex(1) + referenceDecimals(1) + relativeIndex(1) + relativeDecimals(1)
        //         + minRatio(4) + maxRatio(4) + referenceAdapter(0|20) + relativeAdapter(0|20)
        uint32 minRatio;
        uint32 maxRatio;
        assembly {
            // Skip 32 bytes for memory length + 4 bytes (indices + decimals)
            // minRatio is at offset 4 (0x24 from compValue pointer)
            minRatio := shr(224, mload(add(compValue, 0x24)))
            // maxRatio is at offset 8 (0x28 from compValue pointer)
            maxRatio := shr(224, mload(add(compValue, 0x28)))
        }

        // Validate that at least one ratio is provided
        if (minRatio == 0 && maxRatio == 0) {
            revert IRolesError.WithinRatioNoRatioProvided(i);
        }
    }

    /**
     * @notice Ensures pluck values are extracted before being referenced, in
     *         DFS traversal order
     */
    function _pluckOrder(
        ConditionFlat[] memory conditions,
        uint256 index,
        uint256 visited
    ) private pure returns (uint256) {
        ConditionFlat memory condition = conditions[index];

        if (condition.operator == Operator.Pluck) {
            uint8 pluckIndex = uint8(condition.compValue[0]);
            visited |= (1 << pluckIndex);
        }

        if (condition.operator == Operator.WithinRatio) {
            uint8 referenceIndex = uint8(condition.compValue[0]);

            if ((visited & (1 << referenceIndex)) == 0) {
                revert IRolesError.PluckNotVisitedBeforeRef(
                    index,
                    referenceIndex
                );
            }

            uint8 relativeIndex = uint8(condition.compValue[2]);
            if ((visited & (1 << relativeIndex)) == 0) {
                revert IRolesError.PluckNotVisitedBeforeRef(
                    index,
                    relativeIndex
                );
            }
        }

        (uint256 childStart, uint256 childCount, ) = Topology.childBounds(
            conditions,
            index
        );

        for (uint256 i = 0; i < childCount; ++i) {
            visited = _pluckOrder(conditions, childStart + i, visited);
        }

        return visited;
    }

    function _isWordLike(Encoding encoding) private pure returns (bool) {
        return encoding == Encoding.Static || encoding == Encoding.EtherValue;
    }
}

contracts/core/serialize/Topology.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../types/Types.sol";

/**
 * @title  Topology
 * @notice Library for analyzing the structure of flat condition trees
 *
 * @author gnosisguild
 */
library Topology {
    /**
     * @notice Gets the bounds and counts of children for a given node
     * @param conditions The flat array of conditions
     * @param index The index of the parent node
     *
     * @return childStart The index of the first child (0 if no children)
     * @return childCount The total number of children (structural + non-structural)
     * @return sChildCount The number of structural children only
     */
    function childBounds(
        ConditionFlat[] memory conditions,
        uint256 index
    )
        internal
        pure
        returns (uint256 childStart, uint256 childCount, uint256 sChildCount)
    {
        uint256 len = conditions.length;

        for (uint256 i = index + 1; i < len; ++i) {
            uint256 parent = conditions[i].parent;

            if (parent == index) {
                if (childCount == 0) childStart = i;
                ++childCount;

                // Count structural children
                if (isStructural(conditions, i)) {
                    ++sChildCount;
                }
            } else if (parent > index) {
                break;
            }
        }
    }

    /**
     * @notice Determines if a node is structural
     * @dev A node is structural if it has paramType != None OR any descendant has paramType != None
     */
    function isStructural(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (bool) {
        // EtherValue is an alias for None
        Encoding encoding = conditions[index].paramType;
        if (encoding != Encoding.None && encoding != Encoding.EtherValue) {
            return true;
        }

        // Check all direct children
        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index) {
                // Recursively check if child is structural
                if (isStructural(conditions, i)) {
                    return true;
                }
            } else if (conditions[i].parent > index) {
                break;
            }
        }

        return false;
    }
}

contracts/core/serialize/TypeTree.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Topology.sol";

import "../../types/Types.sol";

/**
 * @title  TypeTree
 * @notice Extracts type trees from flat conditions for use in decoding.
 *         Variants can appear in logical or array nodes and must be handled.
 *         See inline comments for rules
 *
 * @author gnosisguild
 */
library TypeTree {
    /**
     * @notice Extracts type tree for flat node at index
     */
    function inspect(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (Layout memory node) {
        bool isLogical = _isLogical(conditions, index);
        bool isVariant = _isVariant(conditions, index);

        /*
         * Type trees only include structural children
         */
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        /*
         * Non-variant logical nodes: first child defines the type tree,
         * others have the same structure and don't influence the result
         */
        if (isLogical && !isVariant) {
            return inspect(conditions, childStart);
        }

        /*
         * Nodes that are Logical and Variant use Dynamic as a container to
         * indicate the variant. All other nodes use their declared paramType
         */
        node.encoding = (isLogical && isVariant)
            ? Encoding.Dynamic
            : conditions[index].paramType;

        /*
         * For non-variant arrays, the first child serves as a template for
         * all elements. For all other nodes, traverse all structural children
         */
        node.children = new Layout[](
            node.encoding == Encoding.Array && !isVariant ? 1 : sChildCount
        );

        for (uint256 i = 0; i < node.children.length; ++i) {
            node.children[i] = inspect(conditions, childStart + i);
        }

        /*
         * For AbiEncoded: extract leadingBytes from compValue (2 bytes).
         * If compValue is empty, default to 4 (function selector size).
         */
        if (node.encoding == Encoding.AbiEncoded) {
            bytes memory compValue = conditions[index].compValue;
            node.leadingBytes = compValue.length == 0
                ? 4
                : uint16(bytes2(compValue));
        }

        node.inlined = _isInlined(conditions, index);
    }

    /**
     * @notice Computes a unique hash for a type tree structure
     */
    function id(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (bytes32) {
        return _hashTree(inspect(conditions, index));
    }

    function _isLogical(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        return
            conditions[index].operator == Operator.And ||
            conditions[index].operator == Operator.Or;
    }

    function _isVariant(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        bool isArray = conditions[index].paramType == Encoding.Array;
        bool isLogical = _isLogical(conditions, index);

        if (!isArray && !isLogical) {
            return false;
        }

        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );
        if (sChildCount <= 1) return false;

        bytes32 idHash = id(conditions, childStart);
        for (uint256 i = 1; i < sChildCount; ++i) {
            if (idHash != id(conditions, childStart + i)) return true;
        }

        return false;
    }

    function _isInlined(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        Encoding encoding = conditions[index].paramType;
        if (
            encoding == Encoding.Dynamic ||
            encoding == Encoding.Array ||
            encoding == Encoding.AbiEncoded
        ) {
            return false;
        }

        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index && !_isInlined(conditions, i)) {
                return false;
            }
        }
        return true;
    }

    function _hashTree(Layout memory tree) private pure returns (bytes32) {
        if (tree.children.length == 0) {
            return bytes32(uint256(tree.encoding));
        }

        bytes32[] memory childHashes = new bytes32[](tree.children.length);
        for (uint256 i = 0; i < tree.children.length; i++) {
            childHashes[i] = _hashTree(tree.children[i]);
        }

        return
            keccak256(
                abi.encodePacked(bytes32(uint256(tree.encoding)), childHashes)
            );
    }
}

contracts/core/Settlement.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Storage.sol";

import {Consumption} from "../types/Allowance.sol";

/**
 * @title Settlement - Persists allowance and membership consumptions to storage.
 *
 * @dev Allowance balances are written before execution to prevent reentrancy.
 *      On failure, original balances are restored. Membership is written only
 *      on success.
 *
 * @author gnosisguild
 */
abstract contract Settlement is RolesStorage {
    /**
     * @dev Writes new balances to storage before execution. Not final.
     * @param consumptions Allowance consumption records to persist.
     */
    function _flushPrepare(Consumption[] memory consumptions) internal {
        uint256 count = consumptions.length;

        for (uint256 i; i < count; ++i) {
            Consumption memory consumption = consumptions[i];

            assert(consumption.consumed <= consumption.balance);

            _store(
                consumption.allowanceKey,
                consumption.timestamp,
                consumption.balance - consumption.consumed
            );
        }
    }

    /**
     * @dev Commits all pending storage updates.
     *
     * Allowances: emits events on success, restores balances on failure.
     * Membership: writes on success only.
     *
     * @param nextMembership Packed membership data: [start:64][end:64][usesLeft:128].
     *        Use uint256.max for no-op (skip membership update).
     */
    function _flushCommit(
        address sender,
        bytes32 roleKey,
        uint256 nextMembership,
        Consumption[] memory consumptions,
        bool success
    ) internal {
        uint256 count = consumptions.length;
        for (uint256 i; i < count; ++i) {
            Consumption memory consumption = consumptions[i];
            if (success) {
                emit ConsumeAllowance(
                    consumption.allowanceKey,
                    consumption.consumed,
                    consumption.balance - consumption.consumed
                );
            } else {
                _store(
                    consumption.allowanceKey,
                    consumption.timestamp,
                    consumption.balance
                );
            }
        }

        if (success && nextMembership != type(uint256).max) {
            // uint256.max == noop
            roles[roleKey].members[sender] = nextMembership;
            if (nextMembership != 0) {
                emit UpdateRole(
                    roleKey,
                    sender,
                    uint64(nextMembership >> 192),
                    uint64(nextMembership >> 128),
                    uint128(nextMembership)
                );
            } else {
                emit RevokeRole(roleKey, sender);
            }
        }
    }

    /// @dev Stores allowance. Writes only word2, preserving period.
    function _store(
        bytes32 allowanceKey,
        uint64 timestamp_,
        uint128 balance_
    ) private {
        /*
         * Storage Layout (2 words, 64 bytes, 512 bits):
         * ┌────────────────────────────────┬────────────────────────────────┐
         * │           maxRefill            │             refill             │
         * │            128 bits            │            128 bits            │
         * ├────────────────┬───────────────┴───────────────┬────────────────┤
         * │   timestamp    │            balance            │     period     │
         * │    64 bits     │           128 bits            │    64 bits     │
         * └────────────────┴───────────────────────────────┴────────────────┘
         */

        assembly {
            mstore(0x00, allowanceKey)
            mstore(0x20, allowances.slot)

            let slot := add(keccak256(0x00, 0x40), 1)

            sstore(
                slot,
                or(
                    or(shl(192, timestamp_), shl(64, balance_)),
                    and(sload(slot), 0xffffffffffffffff) // mask period (lower 64 bits)
                )
            )
        }
    }
}

contracts/core/Setup.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../common/ScopeConfig.sol";

import "./serialize/ConditionsTransform.sol";
import "./Storage.sol";

import {Clearance} from "../types/Permission.sol";

/*
 * Permission Model
 *
 * Role
 *  │
 *  ├─ members ───────────────────────► who can use this role
 *  │
 *  ├─ targets (address → Clearance)
 *  │   │
 *  │   ├─ Clearance.None ────────────► target blocked (default)
 *  │   │
 *  │   ├─ Clearance.Target ──────────► all functions allowed
 *  │   │                               + ExecutionOptions (send/delegatecall)
 *  │   │
 *  │   └─ Clearance.Function ────────► only specific functions allowed
 *  │                                   (see scopeConfig below)
 *  │
 *  └─ scopeConfig (target + selector → ScopeConfig)
 *      │
 *      │   Used when Clearance.Function (selector != 0)
 *      │   or Clearance.Target with conditions
 *      │
 *      ├─ not set ───────────────────► function blocked
 *      │
 *      ├─ wildcarded ────────────────► function allowed, no parameter checks
 *      │                               + ExecutionOptions
 *      │
 *      └─ scoped ────────────────────► function allowed with conditions
 *                                      + ExecutionOptions
 *                                      + Condition tree (parameter constraints)
 *
 * Allowances (separate storage, referenced by conditions)
 */

/**
 * @title Setup - Configuration and setup functions for Zodiac Roles Mod.
 *
 * @author gnosisguild
 */
abstract contract Setup is RolesStorage {
    /*//////////////////////////////////////////////////////////////
                            INITIALIZATION
    //////////////////////////////////////////////////////////////*/

    function setUp(
        bytes memory initParams
    ) public virtual override initializer {
        (address _owner, address _avatar, address _target) = abi.decode(
            initParams,
            (address, address, address)
        );
        _transferOwnership(_owner);
        avatar = _avatar;
        target = _target;

        setupModules();

        emit RolesModSetup(msg.sender, _owner, _avatar, _target, VERSION);
    }

    /*//////////////////////////////////////////////////////////////
                           ROLE MEMBERSHIP
    //////////////////////////////////////////////////////////////*/

    /// @dev Grants a role to a module with optional session parameters.
    /// @param module Module to grant the role to.
    /// @param roleKey Role to grant.
    /// @param start Start timestamp (0 = immediately valid).
    /// @param end End timestamp (0 = never expires).
    /// @param usesLeft Number of uses (0 = unlimited).
    function grantRole(
        address module,
        bytes32 roleKey,
        uint64 start,
        uint64 end,
        uint128 usesLeft
    ) public onlyOwner {
        end = end != 0 ? end : type(uint64).max;
        usesLeft = usesLeft != 0 ? usesLeft : type(uint128).max;
        roles[roleKey].members[module] =
            (uint256(start) << 192) |
            (uint256(end) << 128) |
            uint256(usesLeft);
        if (!isModuleEnabled(module)) {
            enableModule(module);
        }
        emit GrantRole(roleKey, module, start, end, usesLeft);
    }

    /// @dev Revokes a role from a module.
    /// @param module Module to revoke the role from.
    /// @param roleKey Role to revoke.
    function revokeRole(address module, bytes32 roleKey) public onlyOwner {
        delete roles[roleKey].members[module];
        emit RevokeRole(roleKey, module);
    }

    /// @dev Allows a module to renounce its own role.
    /// @param roleKey Role to renounce.
    function renounceRole(bytes32 roleKey) external {
        delete roles[roleKey].members[msg.sender];
        emit RevokeRole(roleKey, msg.sender);
    }

    /// @dev Batch assigns roles to a module.
    /// @param module Module to assign roles to.
    /// @param roleKeys Roles to assign.
    /// @param memberOf true to grant, false to revoke.
    function assignRoles(
        address module,
        bytes32[] calldata roleKeys,
        bool[] calldata memberOf
    ) external onlyOwner {
        for (uint256 i; i < roleKeys.length; ++i) {
            bytes32 key = roleKeys[i];
            if (memberOf[i]) {
                grantRole(module, key, 0, 0, 0);
            } else {
                revokeRole(module, key);
            }
        }
    }

    /// @dev Sets the default role used for a module if it calls
    ///      execTransactionFromModule() or execTransactionFromModuleReturnData().
    /// @param module Address of the module on which to set default role.
    /// @param roleKey Role to be set as default.
    function setDefaultRole(
        address module,
        bytes32 roleKey
    ) external onlyOwner {
        defaultRoles[module] = roleKey;
        emit SetDefaultRole(module, roleKey);
    }

    /*//////////////////////////////////////////////////////////////
                         TARGET PERMISSIONS
    //////////////////////////////////////////////////////////////*/

    /// @dev Allows transactions to a target address, optionally with conditions.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    /// @param conditions The conditions to enforce on all calls (empty for wildcarded).
    /// @param options designates if a transaction can send ether and/or delegatecall to target.
    function allowTarget(
        bytes32 roleKey,
        address targetAddress,
        ConditionFlat[] calldata conditions,
        ExecutionOptions options
    ) external onlyOwner {
        bytes32 key = bytes32(bytes20(targetAddress)) | (~bytes32(0) >> 160);

        roles[roleKey].clearance[targetAddress] = Clearance.Target;
        roles[roleKey].scopeConfig[key] = conditions.length == 0
            ? ScopeConfig.packAsWildcarded(options)
            : ScopeConfig.pack(
                options,
                ConditionsTransform.packAndStore(conditions)
            );

        emit AllowTarget(roleKey, targetAddress, conditions, options);
    }

    /// @dev Designates only specific functions can be called.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    function scopeTarget(
        bytes32 roleKey,
        address targetAddress
    ) external onlyOwner {
        roles[roleKey].clearance[targetAddress] = Clearance.Function;
        emit ScopeTarget(roleKey, targetAddress);
    }

    /// @dev Removes transactions to a target address.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    function revokeTarget(
        bytes32 roleKey,
        address targetAddress
    ) external onlyOwner {
        delete roles[roleKey].clearance[targetAddress];
        emit RevokeTarget(roleKey, targetAddress);
    }

    /*//////////////////////////////////////////////////////////////
                        FUNCTION PERMISSIONS
    //////////////////////////////////////////////////////////////*/

    /// @dev Specifies the functions that can be called.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    /// @param selector 4 byte function selector.
    /// @param options designates if a transaction can send ether and/or delegatecall to target.
    function allowFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector,
        ExecutionOptions options
    ) external onlyOwner {
        roles[roleKey].scopeConfig[_key(targetAddress, selector)] = ScopeConfig
            .packAsWildcarded(options);

        emit AllowFunction(roleKey, targetAddress, selector, options);
    }

    /// @dev Removes the functions that can be called.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    /// @param selector 4 byte function selector.
    function revokeFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector
    ) external onlyOwner {
        delete roles[roleKey].scopeConfig[_key(targetAddress, selector)];
        emit RevokeFunction(roleKey, targetAddress, selector);
    }

    /// @dev Sets conditions to enforce on calls to the specified target.
    /// @param roleKey identifier of the role to be modified.
    /// @param targetAddress Destination address of transaction.
    /// @param selector 4 byte function selector.
    /// @param conditions The conditions to enforce.
    /// @param options designates if a transaction can send ether and/or delegatecall to target.
    function scopeFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector,
        ConditionFlat[] memory conditions,
        ExecutionOptions options
    ) external onlyOwner {
        address pointer = ConditionsTransform.packAndStore(conditions);

        roles[roleKey].scopeConfig[_key(targetAddress, selector)] = ScopeConfig
            .pack(options, pointer);

        emit ScopeFunction(
            roleKey,
            targetAddress,
            selector,
            conditions,
            options
        );
    }

    /*//////////////////////////////////////////////////////////////
                             ALLOWANCES
    //////////////////////////////////////////////////////////////*/

    function setAllowance(
        bytes32 key,
        uint128 balance,
        uint128 maxRefill,
        uint128 refill,
        uint64 period,
        uint64 timestamp
    ) external onlyOwner {
        maxRefill = maxRefill != 0 ? maxRefill : type(uint128).max;
        timestamp = timestamp != 0 ? timestamp : uint64(block.timestamp);

        allowances[key] = Allowance({
            refill: refill,
            maxRefill: maxRefill,
            period: period,
            timestamp: timestamp,
            balance: balance
        });
        emit SetAllowance(key, balance, maxRefill, refill, period, timestamp);
    }

    /// @dev Updates only the refill parameters of an existing allowance, preserving balance and timestamp.
    /// @param key The allowance key.
    /// @param maxRefill Cap at which refilling stops. Pass 0 to set to max uint128.
    /// @param refill Amount added to balance each period.
    /// @param period Refill interval in seconds.
    function updateAllowance(
        bytes32 key,
        uint128 maxRefill,
        uint128 refill,
        uint64 period
    ) external onlyOwner {
        maxRefill = maxRefill != 0 ? maxRefill : type(uint128).max;
        uint64 timestamp = allowances[key].timestamp;
        uint128 balance = allowances[key].balance;

        allowances[key].refill = refill;
        allowances[key].maxRefill = maxRefill;
        allowances[key].period = period;

        emit SetAllowance(key, balance, maxRefill, refill, period, timestamp);
    }

    /*//////////////////////////////////////////////////////////////
                              ADAPTERS
    //////////////////////////////////////////////////////////////*/

    function setTransactionUnwrapper(
        address to,
        bytes4 selector,
        address adapter
    ) external onlyOwner {
        unwrappers[bytes32(bytes20(to)) | (bytes32(selector) >> 160)] = adapter;
        emit SetUnwrapAdapter(to, selector, adapter);
    }
}

