// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {Status} from "./Authorization.sol";

/**
 * @title IRolesError - All custom errors emitted by the Roles Mod
 *
 * @author gnosisguild
 */
interface IRolesError {
    /*//////////////////////////////////////////////////////////////
                            EXECUTION ERRORS
    //////////////////////////////////////////////////////////////*/

    /// Sender is allowed to make this call, but the internal transaction failed
    error ModuleTransactionFailed();

    /*//////////////////////////////////////////////////////////////
                          AUTHORIZATION ERRORS
    //////////////////////////////////////////////////////////////*/

    /// Sender is not a member of the role
    error NoMembership();

    /// Membership is not yet valid (before start timestamp)
    error MembershipNotYetValid();

    /// Membership has expired (after end timestamp)
    error MembershipExpired();

    /// Function signature too short
    error FunctionSignatureTooShort();

    /// Calldata unwrapping failed
    error MalformedMultiEntrypoint();

    /// Authorization check failed with specified status and info
    error ConditionViolation(Status status, bytes32 info);

    /*//////////////////////////////////////////////////////////////
                            DECODING ERRORS
    //////////////////////////////////////////////////////////////*/

    /// Attempted to read beyond calldata bounds
    error CalldataOutOfBounds();

    /*//////////////////////////////////////////////////////////////
                           INTEGRITY ERRORS
    //////////////////////////////////////////////////////////////*/

    /// Root node is invalid or missing
    error UnsuitableRootNode();

    /// Condition tree is not in BFS (breadth-first search) order
    error NotBFS();

    /// Parameter type is unsuitable for the operator at given index
    error UnsuitableParameterType(uint256 index);

    /// Comparison value is unsuitable for the operator at given index
    error UnsuitableCompValue(uint256 index);

    /// Operator is not supported at given index
    error UnsupportedOperator(uint256 index);

    /// Parent node is unsuitable for the child at given index
    error UnsuitableParent(uint256 index);

    /// Child count is unsuitable for the node at given index
    error UnsuitableChildCount(uint256 index);

    /// Child type tree is unsuitable for the node at given index
    error UnsuitableChildTypeTree(uint256 index);

    /// Non-structural children must appear after all structural children
    error NonStructuralChildrenMustComeLast(uint256 index);

    /// WithinRatio target must resolve to a Static type
    error WithinRatioTargetNotStatic(uint256 index);

    /// WithinRatio requires at least one ratio (min or max) to be provided
    error WithinRatioNoRatioProvided(uint256 index);

    /// Allowance decimals exceed maximum of 18
    error AllowanceDecimalsExceedMax(uint256 index);

    /// Slice child must resolve to Static type
    error SliceChildNotStatic(uint256 index);

    /// WithinRatio references a Pluck index that hasn't been visited yet in DFS order
    error PluckNotVisitedBeforeRef(uint256 index, uint8 pluckIndex);
}

contracts/types/RolesEvent.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {ExecutionOptions} from "./Permission.sol";
import {ConditionFlat} from "./Condition.sol";

/**
 * @title IRolesEvent - All events emitted by the Roles Mod.
 *
 * @author gnosisguild
 */
interface IRolesEvent {
    /*//////////////////////////////////////////////////////////////
                             SETUP EVENTS
    //////////////////////////////////////////////////////////////*/

    /// Emitted when the Roles Mod is initialized
    event RolesModSetup(
        address indexed initiator,
        address indexed owner,
        address indexed avatar,
        address target,
        string version
    );

    /// Emitted when a role is granted to a module
    event GrantRole(
        bytes32 roleKey,
        address module,
        uint64 start,
        uint64 end,
        uint128 usesLeft
    );

    /// Emitted when a role is revoked from a module
    event RevokeRole(bytes32 roleKey, address module);

    /// Emitted when role membership is updated (e.g., uses consumed)
    event UpdateRole(
        bytes32 roleKey,
        address module,
        uint64 start,
        uint64 end,
        uint128 usesLeft
    );

    /// Emitted when the default role is set for a module
    event SetDefaultRole(address module, bytes32 defaultRoleKey);

    /*//////////////////////////////////////////////////////////////
                           PERMISSION EVENTS
    //////////////////////////////////////////////////////////////*/

    /// Emitted when a target address is allowed for a role
    event AllowTarget(
        bytes32 roleKey,
        address targetAddress,
        ConditionFlat[] conditions,
        ExecutionOptions options
    );

    /// Emitted when a target address is revoked from a role
    event RevokeTarget(bytes32 roleKey, address targetAddress);

    /// Emitted when a target is scoped (function-level permissions enabled)
    event ScopeTarget(bytes32 roleKey, address targetAddress);

    /// Emitted when a function is allowed for a role
    event AllowFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector,
        ExecutionOptions options
    );

    /// Emitted when a function is revoked from a role
    event RevokeFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector
    );

    /// Emitted when a function is scoped with conditions
    event ScopeFunction(
        bytes32 roleKey,
        address targetAddress,
        bytes4 selector,
        ConditionFlat[] conditions,
        ExecutionOptions options
    );

    /// Emitted when an allowance is configured
    event SetAllowance(
        bytes32 allowanceKey,
        uint128 balance,
        uint128 maxRefill,
        uint128 refill,
        uint64 period,
        uint64 timestamp
    );

    /// Emitted when a transaction unwrap adapter is set
    event SetUnwrapAdapter(address to, bytes4 selector, address adapter);

    /*//////////////////////////////////////////////////////////////
                           ALLOWANCE EVENTS
    //////////////////////////////////////////////////////////////*/

    /// Emitted when an allowance is consumed during execution
    event ConsumeAllowance(
        bytes32 allowanceKey,
        uint128 consumed,
        uint128 newBalance
    );
}

