// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

interface ISingletonFactory {
    function deploy(
        bytes memory initCode,
        bytes32 salt
    ) external returns (address);
}

library ImmutableStorage {
    address public constant SINGLETON_FACTORY =
        0xce0042B868300000d44A59004Da54A005ffdcf9f;

    function store(bytes memory buffer) internal returns (address pointer) {
        bytes memory bytecode = _creationBytecode(buffer);
        pointer = _calculateAddress(bytecode);

        uint256 size;
        assembly {
            size := extcodesize(pointer)
        }

        if (size == 0) {
            assert(
                pointer ==
                    ISingletonFactory(SINGLETON_FACTORY).deploy(
                        bytecode,
                        bytes32(0)
                    )
            );
        }
    }

    /**
     * @notice Reads the runtime bytecode stored at a `pointer` contract, skipping the first byte.
     *
     * @param pointer The address of the deployed pointer contract.
     * @return buffer The raw bytecode (minus the prepended 0x00) read from `pointer`.
     */
    function load(address pointer) internal view returns (bytes memory buffer) {
        assembly {
            let size := sub(extcodesize(pointer), 1)
            // free memory point ought to be multiple of 32
            let rounded := and(add(size, 31), not(31))

            // Get free pointer
            buffer := mload(0x40)
            // Store length
            mstore(buffer, size)
            // Update free pointer
            mstore(0x40, add(add(buffer, 0x20), rounded))

            // Copy code starting from offset 0x01 (skip the 0x00 prefix)
            extcodecopy(pointer, add(buffer, 0x20), 0x01, size)
        }
    }

    /**
     * @notice Generates creation bytecode that deploys a contract containing `data` as its runtime bytecode.
     * @dev The generated constructor copies `data` into memory and returns it as the contract's code.
     *      A leading `0x00` byte is prepended so the resulting contract cannot be called.
     *
     * Assembly layout (constructor):
     *
     * ```
     * 0x00    63 <XXXXXX>   PUSH4 code_size         ; push code length (runtime size)
     * 0x01    80             DUP1                   ; duplicate size
     * 0x02    60 0e          PUSH1 0x0e             ; offset of actual code
     * 0x03    60 00          PUSH1 0x00             ; destination offset
     * 0x04    39             CODECOPY               ; copy <code_size> bytes from offset 0x0e
     * 0x05    60 00          PUSH1 0x00             ; return offset
     * 0x06    f3             RETURN                 ; return code as runtime bytecode
     * <CODE>                                        ; runtime code (0x00 + data)
     * ```
     *
     */
    function _creationBytecode(
        bytes memory data
    ) private pure returns (bytes memory) {
        return
            abi.encodePacked(
                hex"63",
                uint32(data.length + 1),
                hex"80_60_0E_60_00_39_60_00_F3",
                // Prepend 0x00 to prevent the deployed contract from being callable
                hex"00",
                data
            );
    }

    function _calculateAddress(
        bytes memory creationBytecode
    ) private pure returns (address) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                SINGLETON_FACTORY,
                bytes32(0),
                keccak256(creationBytecode)
            )
        );
        return address(uint160(uint256(hash)));
    }
}

contracts/core/serialize/ConditionPacker.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../types/Types.sol";

/**
 * ScopedFunction Layout in Contract Storage
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ HEADER (3 bytes)                                                    │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ • layoutOffset             16 bits (0-65535)                        │
 * │ • maxPluckIndex             8 bits (0-255)                          │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ CONDITION TREE (at conditionTreeOffset)                             │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Header (2 bytes):                                                   │
 * │   • nodeCount              16 bits (0-65535)                        │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Nodes (nodeCount × 5 bytes each):                                   │
 * │   Each node (40 bits):                                              │
 * │     • operator              5 bits                                  │
 * │     • unused                3 bits                                  │
 * │     • childCount            8 bits  (0-255)                         │
 * │     • sChildCount           8 bits  (0-255)                         │
 * │     • compValueOffset      16 bits  (0 if no value)                 │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ CompValues (variable length):                                       │
 * │   For each condition with operator >= EqualTo:                      │
 * │     • length               16 bits  (compValue byte length)         │
 * │     • data                 N bytes  (actual compValue data)         │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * ┌─────────────────────────────────────────────────────────────────────┐
 * │ LAYOUT TREE (at layoutOffset)                                       │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Header (2 bytes):                                                   │
 * │   • nodeCount              16 bits (0-65535)                        │
 * ├─────────────────────────────────────────────────────────────────────┤
 * │ Nodes (nodeCount × 3 bytes each):                                   │
 * │   Each node (24 bits = 3 bytes):                                    │
 * │     • encoding              3 bits                                  │
 * │     • inlined               1 bit                                   │
 * │     • childCount            8 bits                                  │
 * │     • leadingBytes         12 bits                                  │
 * └─────────────────────────────────────────────────────────────────────┘
 *
 * NOTES:
 * - All offsets are relative to the start of the buffer
 * - Offsets are in bytes
 * - Children are implicitly sequential in BFS order (no childrenStart needed)
 */

library ConditionPacker {
    // ═══════════════════════════════════════════════════════════════════════════
    // Constants
    // ═══════════════════════════════════════════════════════════════════════════

    uint256 private constant CONDITION_NODE_BYTES = 5;
    uint256 private constant LAYOUT_NODE_BYTES = 3;

    // ═══════════════════════════════════════════════════════════════════════════
    // Main Packing Function
    // ═══════════════════════════════════════════════════════════════════════════

    function pack(
        ConditionFlat[] memory conditions,
        Layout memory layout
    ) internal pure returns (bytes memory buffer) {
        uint256 layoutOffset = 3 + _conditionPackedSize(conditions);

        buffer = new bytes(layoutOffset + _layoutPackedSize(layout));

        _packConditions(conditions, buffer, 3);
        _packLayout(layout, buffer, layoutOffset);

        // Header: layoutOffset (2 bytes) + maxPluckIndex (1 byte)
        buffer[0] = bytes1(uint8(layoutOffset >> 8));
        buffer[1] = bytes1(uint8(layoutOffset));
        buffer[2] = bytes1(_maxPluckIndex(conditions));
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Condition Packing
    // ═══════════════════════════════════════════════════════════════════════════

    function _conditionPackedSize(
        ConditionFlat[] memory conditions
    ) private pure returns (uint256 result) {
        uint256 count = conditions.length;

        // Header (2 bytes) + nodes
        result = 2 + count * CONDITION_NODE_BYTES;

        // Add space for compValues
        for (uint256 i; i < count; ++i) {
            if (!_hasCompValue(conditions[i])) continue;

            // 2 bytes for length field
            result += 2;

            if (conditions[i].paramType == Encoding.AbiEncoded) {
                result += conditions[i].compValue.length - 2; // skip leadingBytes
            } else if (
                conditions[i].operator == Operator.EqualTo &&
                conditions[i].compValue.length > 32
            ) {
                result += 32; // store keccak256 hash
            } else {
                result += conditions[i].compValue.length;
            }
        }
    }

    function _packConditions(
        ConditionFlat[] memory conditions,
        bytes memory buffer,
        uint256 offset
    ) private pure {
        offset += _packUInt16(conditions.length, buffer, offset);

        uint256 tailOffset = offset +
            (conditions.length * CONDITION_NODE_BYTES);

        for (uint256 i; i < conditions.length; ++i) {
            (, uint256 childCount, uint256 sChildCount) = _childBounds(
                conditions,
                i
            );

            ConditionFlat memory condition = conditions[i];
            bool hasCompValue = _hasCompValue(condition);

            // Pack Node
            {
                // byte 1: operator (5 bits) shifted left, 3 trailing unused bits
                buffer[offset++] = bytes1(uint8(conditions[i].operator) << 3);
                // byte 2
                buffer[offset++] = bytes1(uint8(childCount));
                // byte 3
                buffer[offset++] = bytes1(uint8(sChildCount));

                // bytes 4-5: compValueOffset (0 if no compValue)
                buffer[offset++] = hasCompValue
                    ? bytes1(uint8(tailOffset >> 8))
                    : bytes1(0);
                buffer[offset++] = hasCompValue
                    ? bytes1(uint8(tailOffset))
                    : bytes1(0);

                // todo encode a test with a high number of compValues, make sure both bytes on the tailOffset are considered
            }

            if (!hasCompValue) continue;

            // Pack CompValue - three cases:
            // 1. AbiEncoded match bytes: store N bytes (skip first 2 bytes of compValue)
            // 2. EqualTo with >32 bytes: store keccak256 hash (32 bytes)
            // 3. Other comparisons: store compValue as-is
            bytes memory compValue = condition.compValue;
            uint256 length;
            uint256 srcOffset;
            if (condition.paramType == Encoding.AbiEncoded) {
                length = compValue.length - 2;
                srcOffset = 2; // skip leadingBytes
            } else if (
                condition.operator == Operator.EqualTo && compValue.length > 32
            ) {
                compValue = abi.encodePacked(keccak256(compValue));
                length = 32;
                srcOffset = 0;
            } else {
                length = compValue.length;
                srcOffset = 0;
            }

            buffer[tailOffset++] = bytes1(uint8(length >> 8));
            buffer[tailOffset++] = bytes1(uint8(length));

            assembly {
                let src := add(add(0x20, compValue), srcOffset)
                let dst := add(add(0x20, buffer), tailOffset)
                mcopy(dst, src, length)
            }
            tailOffset += length;
        }
    }

    function _hasCompValue(
        ConditionFlat memory condition
    ) private pure returns (bool) {
        return
            condition.operator >= Operator.EqualTo ||
            condition.operator == Operator.Slice ||
            condition.operator == Operator.Pluck ||
            (condition.paramType == Encoding.AbiEncoded &&
                condition.compValue.length > 2);
    }

    function _maxPluckIndex(
        ConditionFlat[] memory conditions
    ) private pure returns (uint8 result) {
        for (uint256 i = 0; i < conditions.length; ++i) {
            if (conditions[i].operator != Operator.Pluck) continue;

            uint8 index = uint8(conditions[i].compValue[0]);
            if (index > result) {
                result = index;
            }
        }
    }

    function _childBounds(
        ConditionFlat[] memory conditions,
        uint256 index
    )
        private
        pure
        returns (uint256 childStart, uint256 childCount, uint256 sChildCount)
    {
        for (uint256 i = index + 1; i < conditions.length; ++i) {
            uint256 parent = conditions[i].parent;

            if (parent == index) {
                if (childCount == 0) childStart = i;
                ++childCount;
            } else if (parent > index) {
                break;
            }
        }

        sChildCount = childCount;
        for (uint256 i = childStart + childCount; i > childStart; --i) {
            if (_isNonStructural(conditions, i - 1)) {
                // non structural come last
                --sChildCount;
            } else {
                // break once structural, all the rest will also be
                break;
            }
        }
    }

    function _isNonStructural(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        // NonStructural if paramType is None and all descendants are None
        if (conditions[index].paramType != Encoding.None) {
            return false;
        }

        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index) {
                if (!_isNonStructural(conditions, i)) {
                    return false;
                }
            } else if (conditions[i].parent > index) {
                break;
            }
        }

        return true;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Layout Packing
    // ═══════════════════════════════════════════════════════════════════════════

    function _layoutPackedSize(
        Layout memory node
    ) private pure returns (uint256 result) {
        // Header (2 bytes) + all nodes (3 bytes each)
        result = 2 + _countNodes(node) * LAYOUT_NODE_BYTES;
    }

    function _packLayout(
        Layout memory tree,
        bytes memory buffer,
        uint256 offset
    ) private pure returns (uint256) {
        LayoutNodeFlat[] memory nodes = _flattenLayout(tree);

        offset += _packUInt16(nodes.length, buffer, offset);

        // Pack all nodes (3 bytes each)
        for (uint256 i; i < nodes.length; ++i) {
            uint24 packed = (uint24(nodes[i].encoding) << 21) |
                (nodes[i].inlined ? uint24(1 << 20) : uint24(0)) |
                (uint24(nodes[i].childCount) << 12) |
                uint24(nodes[i].leadingBytes);

            buffer[offset++] = bytes1(uint8(packed >> 16));
            buffer[offset++] = bytes1(uint8(packed >> 8));
            buffer[offset++] = bytes1(uint8(packed));
        }

        return 2 + nodes.length * LAYOUT_NODE_BYTES;
    }

    function _flattenLayout(
        Layout memory root
    ) internal pure returns (LayoutNodeFlat[] memory) {
        uint256 total = _countNodes(root);
        LayoutNodeFlat[] memory result = new LayoutNodeFlat[](total);

        Layout[] memory queue = new Layout[](total);
        uint256[] memory parents = new uint256[](total);

        queue[0] = root;
        parents[0] = 0;

        uint256 head = 0;
        uint256 tail = 1;
        uint256 current = 0;

        while (head < tail) {
            Layout memory node = queue[head];
            uint256 parent = parents[head];
            head++;

            // Record this node
            result[current] = LayoutNodeFlat({
                encoding: node.encoding,
                parent: parent,
                childCount: node.children.length,
                leadingBytes: uint16(node.leadingBytes),
                inlined: node.inlined
            });

            // Enqueue children
            for (uint256 i = 0; i < node.children.length; ++i) {
                queue[tail] = node.children[i];
                parents[tail] = current;
                tail++;
            }

            current++;
        }

        return result;
    }

    function _countNodes(
        Layout memory node
    ) internal pure returns (uint256 count) {
        count = 1;
        for (uint256 i = 0; i < node.children.length; ++i) {
            count += _countNodes(node.children[i]);
        }
    }

    struct LayoutNodeFlat {
        uint256 parent;
        Encoding encoding;
        uint256 childCount;
        uint16 leadingBytes;
        bool inlined;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // Buffer Helpers
    // ═══════════════════════════════════════════════════════════════════════════

    function _packUInt16(
        uint256 value,
        bytes memory buffer,
        uint256 offset
    ) private pure returns (uint256) {
        assert(value < type(uint16).max);

        buffer[offset] = bytes1(uint8((value >> 8)));
        buffer[offset + 1] = bytes1(uint8((value)));

        return 2;
    }
}

contracts/core/serialize/ConditionsTrans.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../common/ImmutableStorage.sol";

import "./ConditionPacker.sol";
import "./Integrity.sol";
import "./TypeTree.sol";

import "../../types/Types.sol";

/**
 * @title ConditionsTransform
 * @notice Validates, transforms, and stores condition trees as immutable bytecode
 *         that can later be loaded without additional overhead.
 * @author gnosisguild
 */
library ConditionsTransform {
    /**
     * @notice Ensures a breadth-first condition tree is well-formed, packs it
     *         and stores the packed bytes as immutable bytecode.
     *
     * @param conditions The flat condition array in BFS order.
     * @return pointer Address of the contract where the packed conditions are stored.
     */
    function packAndStore(
        ConditionFlat[] memory conditions
    ) external returns (address) {
        Integrity.enforce(conditions);

        _patchEtherValue(conditions);
        _removeExtraneousOffsets(conditions);

        Layout memory layout;
        // For non-structural trees skip TypeTree creation
        if (Topology.isStructural(conditions, 0)) {
            layout = TypeTree.inspect(conditions, 0);
        }

        bytes memory buffer = ConditionPacker.pack(conditions, layout);
        address pointer = ImmutableStorage.store(buffer);

        return pointer;
    }

    /**
     * @dev Patches EtherValue encoding to None after Integrity validation.
     *      This enables downstream detection via payload.size == 0.
     */
    function _patchEtherValue(ConditionFlat[] memory conditions) private pure {
        uint256 count = conditions.length;
        for (uint256 i; i < count; ++i) {
            if (conditions[i].paramType == Encoding.EtherValue) {
                conditions[i].paramType = Encoding.None;
            }
        }
    }

    /**
     * @dev Removes unnecessary offsets from compValue fields of the conditions
     * array. Its purpose is to ensure a consistent API where every `compValue`
     * provided for use in `Operations.EqualTo` is obtained by calling
     * `abi.encode` directly.
     *
     * By removing the leading extraneous offsets this function makes
     * `abi.encode(...)` output line up with the layout produced by Decoder
     * inspection. Without it, callers would need to patch compValues
     * based on whether the payload is fully inline or at offset.
     *
     * @param conditions Array of ConditionFlat structs to normalize
     */
    function _removeExtraneousOffsets(
        ConditionFlat[] memory conditions
    ) private pure {
        uint256 count = conditions.length;
        for (uint256 i; i < count; ++i) {
            if (
                conditions[i].operator == Operator.EqualTo &&
                !_isInline(conditions, i)
            ) {
                bytes memory compValue = conditions[i].compValue;
                uint256 length = compValue.length;
                assembly {
                    compValue := add(compValue, 32)
                    mstore(compValue, sub(length, 32))
                }
                conditions[i].compValue = compValue;
            }
        }
    }

    /**
     * @dev Returns true when the encoded parameter is stored inline (no ABI
     *      offset), recursing through tuple children to ensure every leaf is
     *      inline as well.
     */
    function _isInline(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        Encoding paramType = conditions[index].paramType;
        if (paramType == Encoding.Static) {
            return true;
        } else if (
            paramType == Encoding.Dynamic ||
            paramType == Encoding.Array ||
            paramType == Encoding.AbiEncoded
        ) {
            return false;
        } else {
            // Tuple: check all children recursively
            uint256 length = conditions.length;
            for (uint256 j = index + 1; j < length; ++j) {
                uint8 parent = conditions[j].parent;
                if (parent < index) {
                    continue;
                }

                if (parent > index) {
                    break;
                }

                if (!_isInline(conditions, j)) {
                    return false;
                }
            }
            return true;
        }
    }
}

contracts/core/serialize/Integrity.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Topology.sol";
import "./TypeTree.sol";

import {IRolesError} from "../../types/RolesError.sol";

/**
 * @title Integrity, A library that validates condition integrity, and
 * adherence to the expected input structure and rules.
 *
 * @author gnosisguild
 */
library Integrity {
    function enforce(ConditionFlat[] memory conditions) internal pure {
        _root(conditions);
        _bfs(conditions);
        for (uint256 i = 0; i < conditions.length; ++i) {
            _node(conditions[i], i);
        }
        _parents(conditions);
        _children(conditions);
        _nonStructuralOrdering(conditions);
        _typeTree(conditions);
        _pluckOrder(conditions, 0, 0);
    }

    function _root(ConditionFlat[] memory conditions) private pure {
        uint256 count;

        for (uint256 i; i < conditions.length; ++i) {
            if (conditions[i].parent == i) ++count;
        }
        if (count != 1 || conditions[0].parent != 0) {
            revert IRolesError.UnsuitableRootNode();
        }
    }

    function _bfs(ConditionFlat[] memory conditions) private pure {
        uint256 length = conditions.length;
        // check BFS
        for (uint256 i = 1; i < length; ++i) {
            if (conditions[i - 1].parent > conditions[i].parent) {
                revert IRolesError.NotBFS();
            }

            if (conditions[i].parent > i) {
                revert IRolesError.NotBFS();
            }
        }
    }

    function _node(ConditionFlat memory condition, uint256 index) private pure {
        Operator operator = condition.operator;
        Encoding encoding = condition.paramType;
        bytes memory compValue = condition.compValue;
        if (operator == Operator.Pass) {
            if (condition.compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.And || operator == Operator.Or) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (condition.compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Empty) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Matches) {
            if (
                encoding != Encoding.Tuple &&
                encoding != Encoding.Array &&
                encoding != Encoding.AbiEncoded
            ) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // For AbiEncoded, compValue must be:
            //    - empty (leadingBytes defaults to 4)
            //    - 2 bytes (leadingBytes, any offset)
            //    - 2 + N bytes (leadingBytes + N match bytes, N <= 32)
            // For Tuple/Array: compValue must be empty
            if (encoding == Encoding.AbiEncoded) {
                uint16 leadingBytes = compValue.length >= 2
                    ? uint16(bytes2(compValue))
                    : 0;
                bool valid = compValue.length == 0 ||
                    compValue.length == 2 ||
                    (compValue.length == 2 + leadingBytes &&
                        leadingBytes <= 32);

                if (!valid) {
                    revert IRolesError.UnsuitableCompValue(index);
                }
            } else if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (
            operator == Operator.ArraySome || operator == Operator.ArrayEvery
        ) {
            if (encoding != Encoding.Array) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.ArrayTailMatches) {
            if (encoding != Encoding.Array) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Slice) {
            // Slice can be applied to Static or Dynamic parameters
            if (encoding != Encoding.Static && encoding != Encoding.Dynamic) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue must be 3 bytes: 2 bytes shift + 1 byte size (1-32)
            if (compValue.length != 3) {
                revert IRolesError.UnsuitableCompValue(index);
            }
            uint8 size = uint8(compValue[2]);
            if (size == 0 || size > 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Pluck) {
            // Pluck must be Static or EtherValue
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue is the index (1 byte, 0-255)
            if (compValue.length != 1) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.EqualToAvatar) {
            if (encoding != Encoding.Static) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.EqualTo) {
            if (
                encoding != Encoding.Static &&
                encoding != Encoding.Dynamic &&
                encoding != Encoding.Tuple &&
                encoding != Encoding.Array &&
                encoding != Encoding.EtherValue
            ) {
                revert IRolesError.UnsuitableParameterType(index);
            }

            bool unsuitable;
            if (_isWordLike(encoding)) {
                unsuitable = compValue.length != 32;
            } else if (encoding == Encoding.Dynamic) {
                unsuitable = compValue.length == 0;
            } else {
                unsuitable = compValue.length < 32;
            }

            if (unsuitable) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (
            operator == Operator.GreaterThan ||
            operator == Operator.LessThan ||
            operator == Operator.SignedIntGreaterThan ||
            operator == Operator.SignedIntLessThan
        ) {
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length != 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Bitmask) {
            if (encoding != Encoding.Static && encoding != Encoding.Dynamic) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // compValue layout: | 2 bytes: shift | N bytes: mask | N bytes: expected |
            if (compValue.length < 4 || (compValue.length - 2) % 2 != 0) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.Custom) {
            if (compValue.length < 20) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.WithinAllowance) {
            if (!_isWordLike(encoding)) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // 32 bytes: allowanceKey only (legacy)
            // 54 bytes: allowanceKey + adapter + accrueDecimals + paramDecimals
            if (compValue.length != 32 && compValue.length != 54) {
                revert IRolesError.UnsuitableCompValue(index);
            }
            if (compValue.length == 54) {
                if (uint8(compValue[52]) > 18 || uint8(compValue[53]) > 18) {
                    revert IRolesError.AllowanceDecimalsExceedMax(index);
                }
            }
        } else if (operator == Operator.CallWithinAllowance) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            // CallWithinAllowance always uses value=1, price adapter doesn't make sense
            if (compValue.length != 32) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else if (operator == Operator.WithinRatio) {
            if (encoding != Encoding.None) {
                revert IRolesError.UnsuitableParameterType(index);
            }
            if (compValue.length < 32 || compValue.length > 52) {
                revert IRolesError.UnsuitableCompValue(index);
            }
        } else {
            revert IRolesError.UnsupportedOperator(index);
        }
    }

    function _parents(ConditionFlat[] memory conditions) private pure {
        uint256 length = conditions.length;

        for (uint256 i = 0; i < length; ++i) {
            if (conditions[i].operator == Operator.CallWithinAllowance) {
                if (conditions[i].parent != i) {
                    (, , uint256 countOtherNodes) = _countParentNodes(
                        conditions,
                        i
                    );

                    if (countOtherNodes > 0) {
                        revert IRolesError.UnsuitableParent(i);
                    }
                }
            } else if (conditions[i].operator == Operator.Empty) {
                if (conditions[i].parent != i) {
                    (
                        uint256 countAbiEncoded,
                        ,
                        uint256 countOtherNodes
                    ) = _countParentNodes(conditions, i);

                    if (countAbiEncoded > 0 || countOtherNodes > 0) {
                        revert IRolesError.UnsuitableParent(i);
                    }
                }
            } else if (conditions[i].operator == Operator.WithinRatio) {
                // WithinRatio can be anywhere except as the root node
                if (conditions[i].parent == i) {
                    revert IRolesError.UnsuitableParent(i);
                }
            }
        }
    }

    function _children(ConditionFlat[] memory conditions) private pure {
        for (uint256 i = 0; i < conditions.length; i++) {
            ConditionFlat memory condition = conditions[i];
            (, uint256 childCount, uint256 sChildCount) = Topology.childBounds(
                conditions,
                i
            );

            if (condition.paramType == Encoding.None) {
                if (
                    (condition.operator == Operator.CallWithinAllowance ||
                        condition.operator == Operator.WithinRatio ||
                        condition.operator == Operator.Empty) && childCount != 0
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
                if (
                    condition.operator == Operator.And ||
                    condition.operator == Operator.Or
                ) {
                    if (childCount == 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Static) {
                // Slice can have at most one child; other Static operators have no children
                if (condition.operator == Operator.Slice) {
                    if (childCount > 1) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                } else {
                    if (childCount != 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Dynamic) {
                // Slice can have at most one child; other Dynamic operators have no children
                if (condition.operator == Operator.Slice) {
                    if (childCount > 1) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                } else {
                    if (childCount != 0) {
                        revert IRolesError.UnsuitableChildCount(i);
                    }
                }
            } else if (condition.paramType == Encoding.Tuple) {
                if (sChildCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else if (condition.paramType == Encoding.AbiEncoded) {
                if (childCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else if (condition.paramType == Encoding.Array) {
                if (sChildCount == 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
                // Enforce only structural children for array iteration operators
                if (
                    (condition.operator == Operator.ArraySome ||
                        condition.operator == Operator.ArrayEvery ||
                        condition.operator == Operator.ArrayTailMatches) &&
                    childCount != sChildCount
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }

                if (
                    (condition.operator == Operator.ArraySome ||
                        condition.operator == Operator.ArrayEvery) &&
                    childCount != 1
                ) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            } else {
                assert(condition.paramType == Encoding.EtherValue);
                // EtherValue is non-structural, no children allowed
                if (childCount != 0) {
                    revert IRolesError.UnsuitableChildCount(i);
                }
            }
        }
    }

    function _nonStructuralOrdering(
        ConditionFlat[] memory conditions
    ) private pure {
        for (uint256 i = 0; i < conditions.length; ++i) {
            (uint256 childStart, uint256 childCount, ) = Topology.childBounds(
                conditions,
                i
            );

            if (childCount == 0) continue;

            // Once we see a non-structural child, all remaining must be non-structural
            bool seenNonStructural = false;
            for (uint256 j = 0; j < childCount; j++) {
                uint256 childIndex = childStart + j;
                bool isStructural = Topology.isStructural(
                    conditions,
                    childIndex
                );

                if (seenNonStructural && isStructural) {
                    // Structural child found after non-structural child
                    revert IRolesError.NonStructuralChildrenMustComeLast(i);
                }

                if (!isStructural) {
                    seenNonStructural = true;
                }
            }
        }
    }

    function _typeTree(ConditionFlat[] memory conditions) private pure {
        for (uint256 i = 0; i < conditions.length; ++i) {
            ConditionFlat memory condition = conditions[i];
            if (
                condition.operator == Operator.And ||
                condition.operator == Operator.Or ||
                condition.paramType == Encoding.Array
            ) {
                if (
                    !_isTypeMatch(conditions, i) &&
                    !_isTypeEquivalence(conditions, i)
                ) {
                    revert IRolesError.UnsuitableChildTypeTree(i);
                }
            }

            _slice(conditions, i);
            _withinRatio(conditions, i);
        }
    }

    function _isTypeMatch(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        if (sChildCount == 1) {
            return true;
        }

        bytes32 id = TypeTree.id(conditions, childStart);
        for (uint256 i = 1; i < sChildCount; ++i) {
            if (id != TypeTree.id(conditions, childStart + i)) return false;
        }

        return true;
    }
    function _isTypeEquivalence(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        for (uint256 i = 0; i < sChildCount; ++i) {
            Encoding encoding = TypeTree
                .inspect(conditions, childStart + i)
                .encoding;
            if (
                encoding != Encoding.Dynamic && encoding != Encoding.AbiEncoded
            ) {
                return false;
            }
        }
        return true;
    }

    function _countParentNodes(
        ConditionFlat[] memory conditions,
        uint256 i
    )
        private
        pure
        returns (
            uint256 countAbiEncoded,
            uint256 countLogical,
            uint256 countOther
        )
    {
        for (uint256 j = conditions[i].parent; ; ) {
            bool isAbiEncoded = conditions[j].paramType == Encoding.AbiEncoded;
            bool isLogical = (conditions[j].operator == Operator.And ||
                conditions[j].operator == Operator.Or);

            if (isAbiEncoded) {
                ++countAbiEncoded;
            }

            if (isLogical) {
                ++countLogical;
            }

            if (!isAbiEncoded && !isLogical) {
                ++countOther;
            }

            if (j == 0) {
                break;
            }

            j = conditions[j].parent;
        }
    }

    function _slice(ConditionFlat[] memory conditions, uint256 i) private pure {
        if (conditions[i].operator != Operator.Slice) {
            return;
        }

        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            i
        );

        // Slice must have exactly one child that resolves to Static
        if (sChildCount == 0) {
            revert IRolesError.SliceChildNotStatic(i);
        }

        Layout memory layout = TypeTree.inspect(conditions, childStart);
        if (layout.encoding != Encoding.Static) {
            revert IRolesError.SliceChildNotStatic(i);
        }
    }

    /**
     * @notice Validates WithinRatio operator constraints
     * @dev Checks that at least one ratio bound (min or max) is provided
     */
    function _withinRatio(
        ConditionFlat[] memory conditions,
        uint256 i
    ) private pure {
        if (conditions[i].operator != Operator.WithinRatio) {
            return;
        }

        bytes memory compValue = conditions[i].compValue;

        // Layout: referenceIndex(1) + referenceDecimals(1) + relativeIndex(1) + relativeDecimals(1)
        //         + minRatio(4) + maxRatio(4) + referenceAdapter(0|20) + relativeAdapter(0|20)
        uint32 minRatio;
        uint32 maxRatio;
        assembly {
            // Skip 32 bytes for memory length + 4 bytes (indices + decimals)
            // minRatio is at offset 4 (0x24 from compValue pointer)
            minRatio := shr(224, mload(add(compValue, 0x24)))
            // maxRatio is at offset 8 (0x28 from compValue pointer)
            maxRatio := shr(224, mload(add(compValue, 0x28)))
        }

        // Validate that at least one ratio is provided
        if (minRatio == 0 && maxRatio == 0) {
            revert IRolesError.WithinRatioNoRatioProvided(i);
        }
    }

    /**
     * @notice Ensures pluck values are extracted before being referenced, in
     *         DFS traversal order
     */
    function _pluckOrder(
        ConditionFlat[] memory conditions,
        uint256 index,
        uint256 visited
    ) private pure returns (uint256) {
        ConditionFlat memory condition = conditions[index];

        if (condition.operator == Operator.Pluck) {
            uint8 pluckIndex = uint8(condition.compValue[0]);
            visited |= (1 << pluckIndex);
        }

        if (condition.operator == Operator.WithinRatio) {
            uint8 referenceIndex = uint8(condition.compValue[0]);

            if ((visited & (1 << referenceIndex)) == 0) {
                revert IRolesError.PluckNotVisitedBeforeRef(
                    index,
                    referenceIndex
                );
            }

            uint8 relativeIndex = uint8(condition.compValue[2]);
            if ((visited & (1 << relativeIndex)) == 0) {
                revert IRolesError.PluckNotVisitedBeforeRef(
                    index,
                    relativeIndex
                );
            }
        }

        (uint256 childStart, uint256 childCount, ) = Topology.childBounds(
            conditions,
            index
        );

        for (uint256 i = 0; i < childCount; ++i) {
            visited = _pluckOrder(conditions, childStart + i, visited);
        }

        return visited;
    }

    function _isWordLike(Encoding encoding) private pure returns (bool) {
        return encoding == Encoding.Static || encoding == Encoding.EtherValue;
    }
}

contracts/core/serialize/Topology.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "../../types/Types.sol";

/**
 * @title  Topology
 * @notice Library for analyzing the structure of flat condition trees
 *
 * @author gnosisguild
 */
library Topology {
    /**
     * @notice Gets the bounds and counts of children for a given node
     * @param conditions The flat array of conditions
     * @param index The index of the parent node
     *
     * @return childStart The index of the first child (0 if no children)
     * @return childCount The total number of children (structural + non-structural)
     * @return sChildCount The number of structural children only
     */
    function childBounds(
        ConditionFlat[] memory conditions,
        uint256 index
    )
        internal
        pure
        returns (uint256 childStart, uint256 childCount, uint256 sChildCount)
    {
        uint256 len = conditions.length;

        for (uint256 i = index + 1; i < len; ++i) {
            uint256 parent = conditions[i].parent;

            if (parent == index) {
                if (childCount == 0) childStart = i;
                ++childCount;

                // Count structural children
                if (isStructural(conditions, i)) {
                    ++sChildCount;
                }
            } else if (parent > index) {
                break;
            }
        }
    }

    /**
     * @notice Determines if a node is structural
     * @dev A node is structural if it has paramType != None OR any descendant has paramType != None
     */
    function isStructural(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (bool) {
        // EtherValue is an alias for None
        Encoding encoding = conditions[index].paramType;
        if (encoding != Encoding.None && encoding != Encoding.EtherValue) {
            return true;
        }

        // Check all direct children
        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index) {
                // Recursively check if child is structural
                if (isStructural(conditions, i)) {
                    return true;
                }
            } else if (conditions[i].parent > index) {
                break;
            }
        }

        return false;
    }
}

contracts/core/serialize/TypeTree.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "./Topology.sol";

import "../../types/Types.sol";

/**
 * @title  TypeTree
 * @notice Extracts type trees from flat conditions for use in decoding.
 *         Variants can appear in logical or array nodes and must be handled.
 *         See inline comments for rules
 *
 * @author gnosisguild
 */
library TypeTree {
    /**
     * @notice Extracts type tree for flat node at index
     */
    function inspect(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (Layout memory node) {
        bool isLogical = _isLogical(conditions, index);
        bool isVariant = _isVariant(conditions, index);

        /*
         * Type trees only include structural children
         */
        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );

        /*
         * Non-variant logical nodes: first child defines the type tree,
         * others have the same structure and don't influence the result
         */
        if (isLogical && !isVariant) {
            return inspect(conditions, childStart);
        }

        /*
         * Nodes that are Logical and Variant use Dynamic as a container to
         * indicate the variant. All other nodes use their declared paramType
         */
        node.encoding = (isLogical && isVariant)
            ? Encoding.Dynamic
            : conditions[index].paramType;

        /*
         * For non-variant arrays, the first child serves as a template for
         * all elements. For all other nodes, traverse all structural children
         */
        node.children = new Layout[](
            node.encoding == Encoding.Array && !isVariant ? 1 : sChildCount
        );

        for (uint256 i = 0; i < node.children.length; ++i) {
            node.children[i] = inspect(conditions, childStart + i);
        }

        /*
         * For AbiEncoded: extract leadingBytes from compValue (2 bytes).
         * If compValue is empty, default to 4 (function selector size).
         */
        if (node.encoding == Encoding.AbiEncoded) {
            bytes memory compValue = conditions[index].compValue;
            node.leadingBytes = compValue.length == 0
                ? 4
                : uint16(bytes2(compValue));
        }

        node.inlined = _isInlined(conditions, index);
    }

    /**
     * @notice Computes a unique hash for a type tree structure
     */
    function id(
        ConditionFlat[] memory conditions,
        uint256 index
    ) internal pure returns (bytes32) {
        return _hashTree(inspect(conditions, index));
    }

    function _isLogical(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        return
            conditions[index].operator == Operator.And ||
            conditions[index].operator == Operator.Or;
    }

    function _isVariant(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        bool isArray = conditions[index].paramType == Encoding.Array;
        bool isLogical = _isLogical(conditions, index);

        if (!isArray && !isLogical) {
            return false;
        }

        (uint256 childStart, , uint256 sChildCount) = Topology.childBounds(
            conditions,
            index
        );
        if (sChildCount <= 1) return false;

        bytes32 idHash = id(conditions, childStart);
        for (uint256 i = 1; i < sChildCount; ++i) {
            if (idHash != id(conditions, childStart + i)) return true;
        }

        return false;
    }

    function _isInlined(
        ConditionFlat[] memory conditions,
        uint256 index
    ) private pure returns (bool) {
        Encoding encoding = conditions[index].paramType;
        if (
            encoding == Encoding.Dynamic ||
            encoding == Encoding.Array ||
            encoding == Encoding.AbiEncoded
        ) {
            return false;
        }

        for (uint256 i = index + 1; i < conditions.length; ++i) {
            if (conditions[i].parent == index && !_isInlined(conditions, i)) {
                return false;
            }
        }
        return true;
    }

    function _hashTree(Layout memory tree) private pure returns (bytes32) {
        if (tree.children.length == 0) {
            return bytes32(uint256(tree.encoding));
        }

        bytes32[] memory childHashes = new bytes32[](tree.children.length);
        for (uint256 i = 0; i < tree.children.length; i++) {
            childHashes[i] = _hashTree(tree.children[i]);
        }

        return
            keccak256(
                abi.encodePacked(bytes32(uint256(tree.encoding)), childHashes)
            );
    }
}

contracts/types/Allowance.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

/**
 * @notice Represents an allowance with periodic refilling capability.
 *
 * @dev The struct layout is storage-optimized: fields updated together during
 * accrual (`balance` and `timestamp`) share word2, minimizing SSTORE
 *
 * Storage Layout (2 words, 64 bytes, 512 bits):
 * ┌────────────────────────────────┬────────────────────────────────┐
 * │           maxRefill            │             refill             │
 * │            128 bits            │            128 bits            │
 * ├────────────────┬───────────────┴───────────────┬────────────────┤
 * │   timestamp    │            balance            │     period     │
 * │    64 bits     │           128 bits            │    64 bits     │
 * └────────────────┴───────────────────────────────┴────────────────┘
 *
 * @custom:member refill Amount added to balance each period.
 * @custom:member maxRefill Cap at which refilling stops.
 * @custom:member period Refill interval in seconds. Zero means one-time use.
 * @custom:member balance Current available allowance.
 * @custom:member timestamp Last refill time.
 */
struct Allowance {
    uint128 refill;
    uint128 maxRefill;
    uint64 period;
    uint128 balance;
    uint64 timestamp;
}

struct Consumption {
    bytes32 allowanceKey;
    uint128 balance;
    uint128 consumed;
    uint64 timestamp;
}

