// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import "@gnosis-guild/zodiac-core/contracts/core/Operation.sol";

import {Consumption} from "./Allowance.sol";

enum Status {
    Ok,
    /// Role not allowed to delegate call to target address
    DelegateCallNotAllowed,
    /// Role not allowed to call target address
    TargetAddressNotAllowed,
    /// Role not allowed to call this function on target address
    FunctionNotAllowed,
    /// Role not allowed to send to target address
    SendNotAllowed,
    /// Or conition not met
    OrViolation,
    /// @deprecated Nor operator has been removed
    _DeprecatedNorViolation,
    /// Parameter value is not equal to allowed
    ParameterNotAllowed,
    /// Parameter value less than allowed
    ParameterLessThanAllowed,
    /// Parameter value greater than maximum allowed by role
    ParameterGreaterThanAllowed,
    /// Parameter value does not match
    ParameterNotAMatch,
    /// Array elements do not meet allowed criteria for every element
    NotEveryArrayElementPasses,
    /// Array elements do not meet allowed criteria for at least one element
    NoArrayElementPasses,
    /// @deprecated ArraySubset operator has been removed
    _DeprecatedParameterNotSubsetOfAllowed,
    /// Bitmask exceeded value length
    BitmaskOverflow,
    /// Bitmask not an allowed value
    BitmaskNotAllowed,
    CustomConditionViolation,
    AllowanceExceeded,
    CallAllowanceExceeded,
    EtherAllowanceExceeded,
    CalldataOverflow,
    RatioBelowMin,
    RatioAboveMax,
    /// Calldata is not empty when it should be
    CalldataNotEmpty,
    /// Leading bytes do not match expected value
    LeadingBytesNotAMatch
}

struct Transaction {
    address to;
    uint256 value;
    Operation operation;
}

struct Context {
    address to;
    uint256 value;
    Operation operation;
    bytes32[] pluckedValues;
}

struct Result {
    Status status;
    Consumption[] consumptions;
    bytes32 info;
}

contracts/types/Condition.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

import {Operator} from "./Operator.sol";

enum Encoding {
    None,
    Static,
    Dynamic,
    Tuple,
    Array,
    AbiEncoded,
    /* EtherValue is a nonstructural, and an alias for None */
    EtherValue
}

struct Condition {
    Operator operator;
    bytes compValue;
    /// @dev Number of children that describe type structure (Tuple/Array fields).
    ///      Structural children come first; non-structural (And/Or/None logic) follow.
    uint256 sChildCount;
    Condition[] children;
}

// This struct is a flattened version of Condition
// used for ABI encoding a scope config tree
// (ABI does not support recursive types)
struct ConditionFlat {
    uint8 parent;
    Encoding paramType;
    Operator operator;
    bytes compValue;
}

struct Layout {
    Encoding encoding;
    Layout[] children;
    /// @dev Bytes to skip before ABI-encoded data begins (e.g., 4 for selector).
    uint256 leadingBytes;
    /// @dev True if this type is encoded inline (includes children)
    bool inlined;
}

struct LayoutFlat {
    uint256 parent;
    Encoding encoding;
}

struct Payload {
    uint256 location;
    uint256 size;
    Payload[] children;
    /* meta flags */
    bool inlined;
    bool variant;
    bool overflow;
}

contracts/types/Operator.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.8.17 <0.9.0;

enum Operator {
    // 00:    EMPTY EXPRESSION (default, always passes)
    //          paramType: Static / Dynamic / Tuple / Array / EtherValue
    //          ❓ children (only for paramType: Tuple / Array to describe their structure)
    //          🚫 compValue
    /* 00: */ Pass,
    // ------------------------------------------------------------
    // 01-03: LOGICAL EXPRESSIONS
    //          paramType: None
    //          ✅ children
    //          🚫 compValue
    /* 01: */ And,
    /* 02: */ Or,
    /* 03: */ _Placeholder03,
    // ------------------------------------------------------------
    // 04:    EMPTY CHECK (passes if data.length == 0)
    //          paramType: None
    //          🚫 children
    //          🚫 compValue
    /* 04: */ Empty,
    // ------------------------------------------------------------
    // 05-12: COMPLEX EXPRESSIONS
    //          paramType: AbiEncoded / Tuple / Array,
    //          ✅ children
    //          🚫 compValue (exception AbiEncoded.Matches uses compValue to define leading bytes)
    /* 05: */ Matches,
    /* 06: */ ArraySome,
    /* 07: */ ArrayEvery,
    /* 08: */ ArrayTailMatches,
    /* 09: */ _Placeholder09,
    /* 10: */ _Placeholder10,
    /* 11: */ _Placeholder11,
    /* 12: */ _Placeholder12,
    // ------------------------------------------------------------
    // 13-14: EXTRACTION EXPRESSIONS
    //          ❓ children (at most one child, must resolve to Static)
    //          ✅ compValue
    /* 13: */ Slice, // paramType: Static / Dynamic, compValue: 3 bytes (2 bytes shift + 1 byte size, 1-32)
    /* 14: */ Pluck, // paramType: Static / EtherValue, compValue: 1 byte (index into pluckedValues, 0-255)
    // ------------------------------------------------------------
    // 15:    SPECIAL COMPARISON (without compValue)
    //          paramType: Static
    //          🚫 children
    //          🚫 compValue
    /* 15: */ EqualToAvatar,
    // ------------------------------------------------------------
    // 16-31: COMPARISON EXPRESSIONS
    //          paramType: Static / Dynamic / Tuple / Array / EtherValue
    //          ❓ children (only for paramType: Tuple / Array to describe their structure)
    //          ✅ compValue
    /* 16: */ EqualTo, // paramType: Static / Dynamic / Tuple / Array / EtherValue
    /* 17: */ GreaterThan, // paramType: Static / EtherValue
    /* 18: */ LessThan, // paramType: Static / EtherValue
    /* 19: */ SignedIntGreaterThan, // paramType: Static / EtherValue
    /* 20: */ SignedIntLessThan, // paramType: Static / EtherValue
    /* 21: */ Bitmask, // paramType: Static / Dynamic
    /* 22: */ Custom, // paramType: Static / Dynamic / Tuple / Array / EtherValue
    /* 23: */ WithinRatio, // paramType: None
    /* 24: */ _Placeholder24,
    /* 25: */ _Placeholder25,
    /* 26: */ _Placeholder26,
    /* 27: */ _Placeholder27,
    /* 28: */ WithinAllowance, // paramType: Static / EtherValue
    /* 29: */ _Placeholder29,
    /* 30: */ CallWithinAllowance, // paramType: None
    /* 31: */ _Placeholder31
}

